INSERT INTO `h_info_tki` VALUES ('10', '1', null, '240', 'Advokasi TKI', 'Advokasi TKI adalah kegiatan yang dilakukan oleh pihak yang diberi kuasa oleh TKI dan atau pihak yang ditugaskan oleh Pemerintah dalam rangka perlindungan (pemberdayaan diri TKI) yang meminta dan atau membutuhkan bantuan advokasi. Kegiatan Advokasi meliputi pemberian nasehat, pemahaman hak-hak dan kewajiban, dan upaya-upaya yang bisa ditempuh dalam rangka memperjuangkan hak-hak TKI.', '4', '21', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-22 16:40:20', '13', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('12', '1', null, '173', 'AEAs', 'Association of Employment Agencies Singapore (AEAs) adalah asosiasi agensi perusahaan jasa tenaga kerja yang berkedudukan di negara Singapura.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('13', '1', null, '173', 'AJASPAC', 'AJASPAC adalah Asosiasi Jasa Penempatan Asia Pasifik (Indonesian Employment Association for Asia Pacific) yang bertujuan memberikan perlindungan dan pengayoman kepada seluruh anggotanya dalam menjalankan usaha, mengembangkan usaha untuk tumbuh dan berkembang dengan semangat kebersamaan yang saling menguntungkan melalui penyaluran aspirasi profesional dibidang pengembangan sumber daya manusia Indonesia khususnya dalam jasa penempatan TKI di luar negeri di kawasan Asia Pasifik dan pada umumnya ke seluruh negara dengan memasuki pasar kerja internasional.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('14', '1', null, '173', 'Akreditasi', 'Adalah kegiatan berupa memberi atau menetapkan tingkat kepercayaan kepada suatu lembaga atau proses untuk menetapkan atau memberi kepercayaan pada suatu lembaga penempatan TKI guna menyelenggarakan suatu kegiatan yang berkaitan dengan penempatan dan perlindungan TKI di luar negeri.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('15', '1', null, '173', 'Akte Lahir', 'Adalah surat keterangan kelahiran seseorang yang diterbitkan oleh kantor catatan sipil. Akte Lahir dipersyaratkan dalam rangka pembuktian umur pencari kerja luar negeri.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('16', '1', null, '173', 'Akte Nikah ', 'Adalah surat keterangan yang dikeluarkan oleh Kantor Urusan Agama atau Kantor Catatan Sipil dan Kependudukan yang menyatakan status pernikahan seseorang sah secara hukum. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('17', '1', null, '173', 'Amah', 'Panggilan atau sebutan bagi tenaga kerja yang bekerja sebagai pekerja rumah tangga (wanita), umumnya digunakan antara lain di Negara Malaysia, Brunei Darussalam dan Negara-negara Timur Tengah.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('18', '1', null, '240', 'Ancaman Kekerasan terhadap TKI', 'Adalah setiap perbuatan secara melawan hukum berupa ucapan, tulisan, gambar, simbol, atau gerakan tubuh, baik dengan atau tanpa menggunakan sarana yang menimbulkan rasa takut atau mengekang kebebasan hakiki seseorang.', '4', '10', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('19', '1', null, '173', 'AP2TKI ', 'Asosiasi Pelaksana Pelatihan Tenaga Kerja Indonesia (AP2TKI) adalah organisasi yang bersifat kolektif, konsultatif, dan independen yang berfungsi sebagai perhimpunan Balai Latihan Kerja Luar Negeri (BLK-LN) dan pusat komunikasi dan informasi tentang berbagai hal yang berkaitan dengan pelatihan tenaga kerja Indonesia.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('20', '1', null, '173', 'AP3TKI ', 'Asosiasi Perusahaan Pelayanan Pemulangan Tenaga Kerja Indonesia (AP3TKI) adalah perhimpunan perusahaan-perusahaan angkutan darat yang  melayani jasa pemulangan TKI dari Gedung Pendataan Kepulangan TKI (GPK-TKI) di Selapajang Tangerang ke daerah asal.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('21', '1', null, '173', 'APJATI ', 'Asosiasi Perusahaan Jasa Tenaga Kerja Indonesia, disingkat APJATI atau disebut juga Indonesian Manpower Service Association  adalah Organisasi Kemasyarakatan atas dasar kesamaan profesi yang dibentuk sebagai satu-satunya wadah berhimpun bagi perusahaan Jasa Tenaga Kerja Indonesia yang memiliki Surat Ijin Usaha Penempatan Tenaga Kerja Indonesia dan bersifat mandiri, nirlaba serta Independen.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('22', '1', null, '10', 'Aptitude Test', 'Adalah pengukuran kemampuan untuk melihat potensi seseorang dalam hal mengatasi masalah, pengambilan keputusan, berpikir cepat, yang terkait dengan potensi yang dimiliki.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2017-04-19 10:36:44', '115', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('23', '1', null, '240', 'Asuransi TKI', 'Asuransi atau Pertanggungan adalah perjanjian antara TKI dengan pihak asuransi dimana pihak asuransi (penanggung) mengikatkan diri kepada calon TKI/TKI (tertanggung), dengan pembayaran premi asuransi oleh tertanggung, untuk adanya jaminan penggantian kepada tertanggung akibat kerugian, kerusakan atau kehilangan. Program Asuransi TKI adalah program asuransi yang diberikan kepada calon TKI/TKI sebelum keberangkatan atau pra penempatan, selama masa penempatan dan purna penempatan ke luar negeri yang diatur dalam peraturan menteri yang bertanggungjawab di bidang ketenagakerjaan.', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('24', '1', null, '10', 'Atase Tenaga Kerja', 'Adalah pejabat yang ditugaskan untuk menangani urusan Tenaga Kerja Indonesia di luar negeri dalam lingkup tugas dan kewenangan Kantor Perwakilan Republik Indonesia di luar negeri.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('25', '1', null, '173', 'ATKI-HK ', 'Asosiasi Tenaga Kerja Indonesia di Hong Kong adalah organisasi massa yang menghimpun pekerja migran Indonesia untuk menegakan hak-hak pekerja migran Indonesia di seluruh dunia. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('26', '1', null, '240', 'Bantuan Hukum', 'Bantuan pembelaan terhadap TKI yang mempunyai permasalahan hukum. Dengan pertimbangan selama masa penempatan TKI di luar negeri dilaksanakan antara lain pemberian bantuan hukum sesuai dengan ketentuan peraturan perundang-undangan di negara tujuan serta hukum dan kebiasaan internasional.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('27', '1', null, '173', 'Berita Acara Rekrut', 'Surat Keterangan berupa Naskah Dinas yang dikeluarkan oleh instansi ketengakerjaan kabupaten/kota yang berisi daftar nama-nama CTKI dan keterangan lainnya, yang direkrut oleh Pelaksana Penempatan Tenaga Kerja Indonesia Swasta (PPTKIS).', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('28', '1', null, '173', 'Biaya Penempatan', 'Adalah biaya yang dibutuhkan untuk keperluan persiapan penempatan TKI untuk bekerja di luar negeri. Biaya penempatan meliputi biaya untuk pengurusan dokumen TKI, pelatihan (bahasa dan kompetensi kerja), pemeriksaan kesehatan dan psikologi TKI, asuransi TKI, pembayaran dana DP3TKI. Biaya penempatan TKI (cost structure) diatur dengan peraturan Menteri yang bertanggung jawab di bidang ketenagakerjaan. ', '4', '16', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('29', '1', null, '173', 'Biaya Rekrut TKI ', 'Biaya-biaya yang dikeluarkan oleh PPTKIS dalam proses rekrut dan seleksi  calon TKI yang terdaftar di Dinas Ketenagakerjaan Kab / Kota untuk ditempatkan di luar negeri. ', '4', '32', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('30', '1', null, '126', 'BKLN', 'Bursa Kerja Luar Negeri yang digagas oleh BNP2TKI yang merupakan lembaga yang menjembatani pencari kerja dan pengguna tenaga kerja guna mempermudah proses rekrutmen, dimulai dari penyebaran informasi, pendataan, penyuluhan, pendaftaran, wawancara, seleksi calon TKI dan fasilitasi informasi calon TKI bagi PPTKIS.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('31', '1', null, '126', 'BLK-LN', 'Balai Latihan Kerja Luar Negeri adalah Lembaga Pendidikan dan Pelatihan untuk mempersiapkan calon TKI guna meningkatkan pengetahuan dan keterampilan kerja, serta mampu mengatasi masalah dan menjadi tenaga kerja yang mandiri, kompeten dan profesional. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('32', '1', null, '10', 'BNP2TKI ', 'Badan Nasional Penempatan dan Perlindungan Tenaga Kerja Indonesia (BNP2TKI) atau disebut Board for Placement and Protection of Indonesian Overseas Workers  adalah Lembaga Pemerintah Non-Departemen yang berada di bawah dan bertanggung jawab kepada Presiden. BNP2TKI dibentuk dalam rangka menjamin serta mempercepat tercapainya tujuan dan sasaran penempatan dan perlindungan TKI di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('33', '1', null, '10', 'BNSP ', 'Badan Nasional Sertifikasi Profesi (BNSP) adalah lembaga independen yang bertugas melaksanakan sertifikasi kompetensi kerja bagi tenaga kerja, baik yang berasal dari lulusan pelatihan kerja dan/atau tenaga kerja yang telah berpengalaman. BNSP mempunyai otoritas dan menjadi rujukan dalam penyelenggaraan sertifikasi kompetensi kerja secara nasional. Keberadaan BNSP erat kaitannya dengan penyiapan tenaga kerja Indonesia yang kompetitif menghadapi persaingan di pasar kerja global, memudahkan kerjasama dengan institusi-institusi sejenis di negara-negara lain serta membangun saling pengakuan (mutual recognition) terhadap kompetensi tenaga kerja masing-masing Negara.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('34', '1', null, '10', 'BP3TKI ', 'Balai Pelayanan Penempatan dan Perlindungan Tenaga Kerja Indonesia (BP3TKI) adalah unit pelaksana teknis dilingkungan BNP2TKI yang berada dibawah dan bertanggung jawab kepada kepala BNP2TKI yang bertempat di ibu kota Provinsi atau tempat pemberangkatan TKI yang dianggap perlu dan mempunyai tugas memberikan kemudahan pelayanan pemprosesan seluruh dokumen penempatan, perlindungan dan penyelesaian masalah tenaga kerja Indonesia secara berkoordinasi dan terintegrasi di wilayah kerja masing-masing. Pada tahun 2010 terdapat 18 BP3TKI yaitu: BP3TKI Banda Aceh ; BP3TKI Medan; BP3TKI Pekanbaru; BP3TKI Tanjung Pinang; BP3TKI Palembang; BP3TKI Serang; BP3TKI Jakarta; BP3TKI Bandung; BP3TKI Semarang; BP3TKI Yogyakarta; BP3TKI Denpasar; BP3TKI Mataram; BP3TKI Kupang; BP3TKI Pontianak; BP3TKI Banjarbaru; BP3TKI Nunukan; BP3TKI Makasar; BP3TKI Manado. ', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('35', '1', null, '240', 'Call Center', 'Adalah suatu sistem yang dikembangkan oleh BNP2TKI dalam rangka menfasilitasi pertanyaan, dan pengaduan yang berkaitan dengan permasalahan dan perlindungan Tenaga Kerja Indonesia. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('36', '1', null, '173', 'Calling Visa', 'Adalah undangan dari pemerintah suatu negara  dengan memberikan Visa kepada mereka yang hendak berkunjung ke Negara tersebut untuk suatu keperluan sehingga mereka yang hendak berkunjung tersebut tidak perlu mengajukan permohonan Visa. Calling Visa  di beberapa Negara adalah pemberian visa tambahan bagi para TKI yang telah menyelesaikan masa kontraknya selama jangka waktu tertentu - untuk kembali bekerja di luar negeri selama beberapa tahun berikutnya dengan memenuhi syarat-syarat yang telah ditetapkan.', '4', '16', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('37', '1', null, '173', 'Calo TKI', 'Pengertian umum Calo adalah orang yang menjadi perantara dengan memberikan jasanya untuk menguruskan sesuatu berdasarkan upah. Dalam penempatan Tenaga Kerja ke luar negeri, istilah Calo/Sponsor TKI adalah orang atau pihak-pihak yang membantu calon TKI dalam pengurusan dokumen untuk bekerja di luar negeri dan mempertemukannya dengan PPTKIS serta memungut upah/biaya. Dalam Undang-undang No. 39/2004 penjelasan pasal 37; PPTKIS tidak dibenarkan melakukan perekrutan melalui calo atau sponsor baik warga Negara Indonesia maupun warga Negara asing.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('38', '1', null, '173', 'Calon Tenaga Kerja Indonesia (CTKI)', 'Calon Tenaga Kerja Indonesia adalah warga negara Indonesia yang akan menjadi tenaga kerja/akan bekerja di luar negeri dalam hubungan kerja untuk jangka waktu tertentu dengan menerima upah. Setiap warga Indonesia yang memenuhi syarat sebagai pencari kerja yang akan bekerja di luar negeri dan terdaftar di instansi pemerintah kabupaten/kota yang bertanggungjawab di bidang ketenagakerjaan.&nbsp;(UU Nomor 39 tahun 2004)', '4', '23', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 11:45:35', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('39', '1', null, '173', 'Care Giver', 'TKI yang bekerja pada pengguna perseorangan sebagai perawat/pengasuh orang tua/lansia di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('40', '1', null, '173', 'Care Worker', 'TKI yang bekerja dalam sebuah lembaga berbadan hukum tetap sebagai perawat/pengasuh orang tua/lansia di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('41', '1', null, '173', 'CIMA', 'Consortium of Indonesian Manning Agencies (CIMA) adalah konsorsium perusahaan pengawakan kapal yang didirikan pada tanggal 12 September 1990. Visinya adalah untuk melindungi pelaut Indonesia yang berkerja diluar negeri, dalam rangka memperluas kesempatan kerja pelaut di luar negeri dan mendidik pelaut dengan memberikan pengalaman kerja pada perusahaan asing. CIMA dibentuk untuk menyatukan, melindungi dan memperjuangkan kepentingan anggotanya serta meningkatkan kemampuan bisnis dalam upaya lebih meningkatkan kemampuan pelaut Indonesia. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('42', '1', null, '10', 'Citizen Services', 'Adalah selanjutnya disebut pelayanan warga Negara Indonesia yang terintegrasi di sejumlah perwakilan RI yang bertujuan untuk meningkatkan perlindungan bagi semua warga Negara Indonesia, termasuk Tenaga Kerja Indonesia yang bekerja di luar negeri. Citizen services juga merupakan sistem terintegrasi yang melibatkan semua unsur di Perwakilan Republik Indonesia yang tidak terpisahkan dari fungsi Konsuler Perwakilan; berada di bawah dan bertanggung jawab kepada Kepala Perwakilan. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('43', '1', null, '240', 'CMR', 'Coalition for Migrants Rights adalah sebuah organisasi yang bergerak dalam bidang advokasi atau pembelaan terhadap hak-hak pekerja migran.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('44', '1', null, '240', 'Crisis Centre TKI', 'Sistem Pelayanan Pengaduan, yang memberikan pelayanan bagi CTKI/TKI, keluarganya atau pihak yang diberi kuasa untuk mengadukan masalah dan upaya penyelesaiannya. Tujuan didirikan Crisis Centre TKI di lingkungan BNP2TKI adalah dalam rangka memberikan perlindungan, advokasi dan bantuan hukum.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-22 17:38:07', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('45', '1', null, '173', 'Cuti TKI', 'Cuti TKI dalam pekerjaan adalah status ketidakhadiran TKI sesuai dengan perjanjian kerja dan/atau sebab lain dalam waktu tertentu dengan alasan yang masuk akal, sah dan telah mendapatkan persetujuan dari pengguna di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 16:36:43', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('46', '1', null, '240', 'Deportasi TKI', 'Adalah tindakan hukum yang dilakukan oleh suatu Negara terhadap TKI yang melakukan pelanggaran peraturan keimigrasian dan/atau dibatalkan izin tinggalnya karena sesuatu dan lain hal di Negara tersebut.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('47', '1', null, '10', 'Deposito Jaminan PPTKIS', 'Adalah dana jaminan TKI pada bank peserta program sebesar 500 juta atas nama menteri yang disetor oleh PPTKIS.', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('48', '1', null, '240', 'Detention Center', 'Adalah tempat penahanan bagi pekerja migran yang dianggap melanggar ketentuan peraturan keimigrasian di suatu negara. Immigration Detention adalah tindakan untuk menahan sementara bagi individu yang menyalahgunakan visa, masuk secara ilegal atau tidak sah. Penahanan sementara dilakukan sampai pihak imigrasi yang berwenang memberikan visa sehingga yang bersangkutan bisa bebas berbaur dengan komunitas setempat atau memulangkannya ke negara asal. Mandatory detention adalah tindakan penahanan paksa atau mengisolasi orang yang mencari suaka politik atau dianggap imigran gelap atau pendatang yang tidak sah di suatu Negara.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('49', '1', null, '240', 'Devisa TKI', 'Mata uang asing yang merupakan upah dari hasil kerja TKI di luar negeri yang dibawa masuk ke wilayah Negara Republik Indonesia secara tunai atau dikirimkan melalui lembaga keuangan non-perbankan dan atau tercatat di bank-bank yang  beroperasi di wilayah Indonesia.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('50', '1', null, '10', 'Disnaker', 'Dinas Tenaga Kerja adalah Satuan Kerja Perangkat Daerah (SKPD) pemerintah propinsi, kabupaten, kota yang bertanggungjawab di bidang ketenagakerjaan. Dalam lingkungan Pemerintah Daerah dikenal ada Disnaker Tingkat Provinsi dan Disanaker Tingkat Kabupaten/Kota.  Dalam Undang-undang No 39 tahun 2004 Tentang Penempatan dan Perlindungan TKI di Luar Negeri ada disebutkan sbb: 1). Pasal 36 Ayat 1 Pencari Kerja yang berminat bekerja ke luar negeri harus terdaftar pada instansi Pemerintah Kabupaten / Kota yang bertanggung jawab di bidang ketenegakerjaan. 2).Pasal 38 Ayat 2 Perjanjian Penempatan sebagaimana dimaksud pada ayat 1 (satu) diketahui oleh intansi yang bertanggung jawab dibidang ketenagakerjaan Kabupaten / Kota. 3).Penjelasan Pasal 37,ketentuan dalam pasal ini berarti bahwa PPTKIS tidak dibenarkan melakukan perekrutan melalui calo atau sponsor baik warga negara Indonesia maupun warga negara asing. 4). Penjelasan Pasal 51 huruf (f); Paspor diterbitkan setelah mendapatkan rekomendasi dinas yang bertanggungjawab di bidang ketenagakerjaan di kabupaten / kota setempat. 5). Pasal 54 ayat 1; PPTKIS wajib melaporkan setiap perjanjian penempatan TKI kepada instansi Pemerintah Kabupaten / Kota yang bertanggungjawab di bidang ketenagakerjaan. 6). Pasal 92 Ayat 1; Pengawasan terhadap penyelenggaraan penempatan dan perlindungan TKI di luar negeri dilaksanakan oleh instansi yang bertanggungjawab di bidang ketenagakerjaan pada Pemerintah, Pemerintah Provinsi, dan Pemerintah kabupaten / kota.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('51', '1', null, '173', 'Dokumen TKI', 'Adalah sejumlah dokumen yang dipersyaratkan wajib dimiliki TKI untuk dapat bekerja di luar negeri. Dokumen TKI meliputi a. Kartu Tanda Penduduk (KTP), ijazah pendidikan terakhir, akte kelahiran atau surat keterangan kenal lahir; b. surat keterangan status perkawinan bagi yang telah menikah melampirkan foto copy buku nikah; c. surat keterangan izin suami atau istri, izin orang tua, atau izin wali; d. sertifikat kompetensi kerja; e. surat keterangan sehat berdasarkan hasil pemeriksaan kesehatan dan psikologi; f. paspor yang diterbitkan oleh kantor imigrasi setempat; g. visa kerja; h. perjanjian penempatan TKI; i. perjanjian kerja; dan j. Kartu Tenaga Kerja Luar Negeri (KTKLN).', '4', '11', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('52', '1', null, '240', 'Eksploitasi Seksual TKI', 'Adalah segala bentuk ekploitasi seksual yang menimpa TKI baik berupa pemanfaatan organ tubuh seksual atau organ tubuh lain dari korban (TKI) untuk mendapatkan keuntungan, termasuk tetapi tidak terbatas pada semua kegiatan pelacuran dan percabulan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('53', '1', null, '240', 'Eksploitasi TKI', 'Adalah tindakan dengan atau tanpa persetujuan korban yang meliputi tetapi tidak terbatas pada pelacuran, kerja atau pelayanan paksa, perbudakan atau praktik serupa perbudakan, penindasan, pemerasan, pemanfaatan fisik, seksual, organ reproduksi, atau secara melawan hukum memindahkan atau mentransplantasi organ dan atau jaringan tubuh atau memanfaatkan tenaga atau kemampuan TKI oleh pihak lain untuk mendapatkan keuntungan baik materiil maupun immateriil.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('54', '1', null, '173', 'Eks-TKI', 'Adalah istilah yang umum digunakan sebagai sebutan bagi Tenaga Kerja yang pernah bekerja di luar negeri.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('55', '1', null, '173', 'EPS', 'Employment Permit System adalah suatu sistem yang mensyaratkan adanya persyaratan-persyaratan tertentu untuk dapat bekerja di suatu Negara. Istilah EPS, saat ini  populer  digunakan terkait dengan persyaratan kerja untuk program penempatan TKI Government to Government (G to G) ke Negara Korea. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('56', '1', null, '173', 'Gaji TKI', 'Adalah Sebagai balas jasa dan penghargaan atas prestasi kerja pekerja yang bersangkutan. Pada umumnya sistem penggajian dapat digolongkan dalam 2 sistem yaitu sistem skala tunggal dan sistem skala ganda. Sistem skala tunggal adalah sistem penggajian yang memberikan gaji yang sama kepada pekerja dengan tidak memperhatikan sifat pekerjaan yang dilakukan dan tanggungjawab pekerjaannya. Sistem skala ganda adalah penggajian yang menentukan besarnya gaji didasarkan atas profesionalisme, prestasi kerja yang dicapai dan beratnya tanggungjawab pekerjaannya.', '4', '30', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('57', '1', null, '173', 'GAMCA', 'The Gulf Cooporation Council (GCC) Acredited medical Clinic Association (GAMCA)  adalah lembaga yang memberikan akreditasi kepada klinik kesehatan di suatu negara untuk melakukan pemeriksaan kesehatan bagi tenaga kerja yang akan bekerja di Negara-negara GCC (Bahrain, Kuwait, Oman, Qatar, Saudi Arabia dan Uni-Emirat Arab.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('58', '1', null, '173', 'General Worker', 'General Worker adalah sebutan status pekerja asing di suatu Negara. Sebagai contoh di Malaysia dibagi 3 kategori untuk tenaga kerja asing yaitu pekerja biasa/umum/operator status mereka General Worker; Pekerja Mahir (skill worker), visa yang diberikan Visit Pass (Temporary Employment), IC (Identity Card) kartu tanda pengenal yang diberikan pada pekerja yang berstatus Skill Worker; Pekerja Profesional, visa yang diberikan Visit Pass (Temporary Employment) berlaku 6 bulan dan tidak memakai IC; Pekerja Level Management, visa yang diberikan Employment Pass, IC yang diberikan berstatus Expatriate. Ini adalah visa kerja tertinggi yang dikeluarkan Imigrasi.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('59', '1', null, '126', 'Global Forum on Migration and Development (GFMD)', 'Forum global tentang migrasi dan pembangunan yang merupakan forum diskusi antara lembaga-lembaga pemerintah dengan masyarakat (civil society). Forum ini pertama kali diselenggarakan di Brussel (Belgia) tanggal 9-11 Juli 2007. Forum ini digagas dan dirancang untuk melihat dampak migrasi dalam pembangunan, memungkinkan pendekatan kebijakan yang lebih konsisten, mengidentifikasi instrumen serta praktek-praktek baru (best practices) yang positif dengan taktik dan metode yang inovatif akhirnya meningkatkan kerjasama antar stakeholder.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('60', '1', null, '126', 'Globalisasi', 'Satu istilah yang memiliki hubungan dengan peningkatan keterkaitan dan ketergantungan antarbangsa dan antarmanusia di seluruh dunia melalui perdagangan, investasi, perjalanan, budaya popular, dan bentuk-bentuk interaksi yang lain sehingga batas-batas suatu negara menjadi semakin sempit. Globalisasi merupakan proses di mana antarindividu, antarkelompok, dan antarnegara saling berinteraksi, bergantung, terkait, dan mempengaruhi satu sama lain yang melintasi batas Negara. Migrasi dan Globalisasi tidak dapat dihindarkan, faktanya masyarakat secara politik, ekonomi, sosial dan kebudayaan merupakan bagian yang tak terpisahkan dari komunitas yang lebih luas.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('61', '1', null, '240', 'GPK-TKI', 'Gedung Pendataan Kepulangan TKI yang selanjutnya disingkat GPK-TKI adalah tempat pelayanan pendataan dan kepulangan TKI purna tugas yang akan kembali pulang ke daerah asal. GPK-TKI terletak di Selapajang Tangerang Provinsi Banten. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('62', '1', null, '173', 'Himsataki ', 'Himpunan Pengusaha Jasa Penempatan TKI adalah perkumpulan pengusaha penyalur jasa penempatan TKI ke luar negeri.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('63', '1', null, '173', 'IDEA', 'Indonesian Employment Agencies Association (IDEA) merupakan suatu wadah dan wahana pembinaan, komunikasi, informasi, representasi, konsultasi, fasilitasi dan advokasi pengusaha penempatan tenaga kerja Indonesia dalam rangka mewujudkan dunia usaha yang kuat dan berdaya saing tinggi yang bertumpu pada keunggulan nyata sumber daya nasional yang memadukan secara seimbang antar potensi ketenagakerjaan nasional yakni antar sektor dan antar daerah dalam dimensi tertib hukum, etika bisnis, kemanusiaan dan kelestarian lingkungan dalam suatu tatanan ekonomi pasar dalam percaturan perekonomian global dengan berbasis pada kekuatan daerah dan hubungan luar negeri serta IDEA bersifat nirlaba, kolektif, mandiri dan independen yang dibentuk atas dasar kesamaan profesi.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('64', '1', null, '126', 'ILO', 'International Labour Organization (ILO) adalah badan Perserikatan Bangsa-Bangsa (PBB) yang terus berupaya mendorong terciptanya peluang bagi perempuan dan laki-laki untuk memperoleh pekerjaan yang layak dan produktif secara bebas, adil, aman dan bermartabat. Tujuan utama ILO adalah mempromosikan hak-hak ditempat kerja, mendorong terciptanya peluang kerja yang layak, meningkatkan perlindungan sosial serta memperkuat dialog untuk mengatasi permasalahan-permasalahan yang terkait dengan dunia kerja.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('65', '1', null, '10', 'Imigrasi', 'Adalah perpindahan orang dari suatu Negara-bangsa (Nation-State) untuk ke Negara lain dimana ia bukan warga Negara. Imigrasi merujuk pada perpindahan untuk menetap, sedangkan yang datang dalam waktu pendek tidak dianggap imigran, walaupun demikian imigrasi pekerja musiman (umumnya untuk kurang dalam satu tahun) sering dianggap sebagai bentuk imigrasi. Imigrasi manusia telah berlangsung selama ribuan tahun, konsep imigrasi modern terkait dengan perkembangan Negara-bangsa dengan kriteria kewarganegaraan yang jelas, paspor, pengawasan perbatasan permanen, serta hukum kewarganegaraan. Kewarganegaraan dari suatu Negara memberikan hak-hak khusus kepada penduduk negara tersebut, sementara para imigran dibatasi oleh hukum imigrasi.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('66', '1', null, '126', 'Informasi Pasar Kerja (IPK)', 'Adalah Informasi tersedianya lowongan kerja atau jabatan-jabatan pada suatu instansi pemerintah / perusahaan / perorangan baik didalam negeri maupun di luar negeri yang sudah terdaftar dan disebar luaskan oleh dinas tenaga kerja kab/kota. ', '4', '36', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('67', '1', null, '10', 'Instansi Ketenagakerjaan ', 'Adalah instansi pemerintahan, pemerintah, pemerintah provinsi, kabupaten/kota yang bertanggung jawab dibidang ketenagakerjaan.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('68', '1', null, '10', 'Instansi Pemerintah Kabupaten/Kota Bidang Ketenagakerjaan', 'Instansi yang mengurusi urusan ketenagakerjaan. Pencari kerja yang berminat bekerja ke luar negeri harus terdaftar pada instansi pemerintah kabupaten/kota yang bertanggung jawab di bidang ketenagakerjaan.', '4', '12', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('69', '1', null, '240', 'Jamsostek', 'Adalah singkatan dari Jaminan Sosial Tenaga Kerja, dan merupakan program publik yang memberikan perlindungan bagi tenaga kerja untuk mengatasi resiko social ekonomi tertentu dan penyelengaraanya menggunakan mekanisme asuransi social. Sebagai program public Jamsostek memberikan hak dan membebani kewajiban secara pasti (Compulsory) bagi pengusaha dan tenaga kerja berdasarkan Undang-undang no 3 tahun 1992 mengatur jenis program jaminan kecelakaan kerja (JKK), jaminan hari tua (JHT), Jaminan Kematian (JKM) dan Jaminan Pemeliharaan Kesehatan (JPK).', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('70', '1', null, '173', 'Job Order TKI', 'Adalah dokumen permintaan Tenaga Kerja Indonesia yang diajukan oleh Mitra Usaha atau Pengguna di luar negeri kepada PPTKIS dan telah disetujui oleh perwakilan RI. Informasi dalam Job Order antara lain, menyangkut kesempatan kerja, jenis jabatan, lokasi kerja,  syarat jabatan, besaran gaji/upah yang ditawarkan, jangka waktu kontrak kerja dan jumlah tenaga kerja yang diminta serta jangka waktu berlakunya job order (permintaan) tersebut. Demand Letter adalah istilah surat permintaan (Job Order) Tenaga Kerja Indonesia yang umumnya digunakan bagi permintaan tenaga kerja dari Mitra Usaha atau Pengguna di suatu negara Penempatan TKI dan digunakan sebagai dasar permohonan Visa kerja.  CCVI adalah singkatan dari Certificate of Confirmation of Visa Issuance yang merupakan calling visa yang disiapkan oleh perusahaan di negara Korea bagi Tenaga Kerja Indonesia yang akan berkerja di negara tersebut. Block Visa adalah surat permintaan sejumlah Tenaga Kerja Indonesia dari Mitra Usaha atau Pengguna di negara Arab Saudi sebagai dasar permohonan visa kerja. VISA Wakallah adalah surat permintaan Tenaga Kerja Indonesia yang dijadikan dasar permohonan Visa kerja ke suatu negara penempatan.', '4', '79', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('71', '1', null, '173', 'Kantor Cabang PPTKIS', 'Adalah Kantor Cabang Perwakilan PPTKIS yang ada di provinsi atau di kabupaten kota yang bertindak untuk dan atas nama PPTKIS Pusat.', '4', '20', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('72', '1', null, '10', 'KBRI', 'Kedutaan Besar Republik Indonesia adalah kantor perwakilan diplomatik Republik Indonesia yang ditempatkan secara permanen di ibukota Negara lain atau lembaga/organisasi internasional (PBB). Pejabat diplomatik tertinggi disebut Duta Besar. Duta Besar dalam melaksanakan penempatan dan perlindungan TKI di luar negeri dibantu oleh Atase Tenaga Kerja.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('73', '1', null, '240', 'Kecelakaan Kerja TKI', 'Kecelakaan kerja TKI pra-penempatan adalah kecelakaan dan sakit TKI selama masa persiapan penempatan. Kecelakaan kerja TKI masa penempatan adalah kecelakaan di tempat kerja TKI, kecelakaan yang menimpa TKI dalam perjalanan berangkat dan pulang ke/dari tempat kerja dan  sakit. Kecelakaan kerja TKI masa purna penempatan adalah kecelakaan yang menimpa TKI dalam perjalanan dari pelabuhan debarkasi ke tempat tinggalnya di daerah asal.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('74', '1', null, '240', 'Kekerasan terhadap TKI', 'Adalah setiap perbuatan secara melawan hukum, dengan atau tanpa menggunakan sarana terhadap fisik dan psikis yang menimbulkan bahaya bagi nyawa, badan, atau menimbulkan terampasnya kemerdekaan seseorang. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('75', '1', null, '240', 'Kepulangan TKI', 'Adalah peristiwa dimana TKI dalam perjalanan kembali ke tempat tinggalnya di daerah asal. Kepulangan TKI bisa diakibatkan karena kontrak kerja berakhir, cuti atau bermasalah. ', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-22 17:39:42', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('76', '1', null, '173', 'Kesehatan CTKI', 'Adalah gambaran tingkat kesehatan fisik dan mental psikologis CTKI untuk bekerja pada jabatan dan negara tertentu sesuai dengan syarat jabatan yang diminta oleh pengguna di negara tujuan. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('77', '1', null, '173', 'Kesehatan Psikologi CTKI', 'Adalah gambaran kesehatan dan psikologi calon TKI  yang meliputi kesehatan dan tingkat kesiapan psikis serta kesesuaian kepribadian calon TKI dengan pekerjaan yang akan dilakukan di negara tujuan.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('78', '1', null, '173', 'Kesejahteraan TKI', 'Adalah menggambarkan tingkat terpenuhinya kebutuhan dan/atau keperluan yang bersifat jasmaniah maupun rohaniah, dalam kurun waktu pra-penempatan, masa-penempatan dan purna-penempatan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('79', '1', null, '10', 'KJRI', 'Konsulat Jenderal Republik Indonesia adalah kantor Perwakilan Konsuler Indonesia pada wilayah tertentu pada Negara asing yang memiliki hubungan diplomatik dengan Indonesia dan dikepalai oleh seorang Konsul Jenderal atau disingkat Konjen dan umumnya secara struktural berada di bawah Kedutaan Besar RI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('80', '1', null, '240', 'Klaim Asuransi TKI', 'Adalah suatu tuntutan dari pihak pemegang Polis yang ditunjuk kepada pihak asuransi atas sejumlah pembayaran Uang Pertanggungan (UP) atau nilai tunai yang timbul karena syarat-syarat dalam perjanjian Asuransi TKI telah dipenuhi.', '4', '14', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('81', '1', null, '173', 'KLPT', 'Korean Language Proficiency Test (KLPT) adalah program test kemampuan berbahasa Korea bagi Calon TKI yang berminat untuk bekerja di Korea melalui program kerjasama antar pemerintah RI dan pemerintah Korea yang dikenal juga dengan program Government to Government (G to G) Korea.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('82', '1', null, '173', 'Kompetensi Kerja TKI', 'Adalah kemampuan kerja TKI yang mencakup aspek pengetahuan, keterampilan kerja, dan sikap kerja yang sesuai dengan standar yang ditetapkan, serta kemampuan berkomunikasi dengan menggunakan bahasa negara tujuan.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('83', '1', null, '240', 'Konseling TKI', 'Adalah proses pemberian bantuan yang dilakukan oleh seorang ahli (Konselor) kepada TKI yang mengalami suatu masalah yang bermuara pada teratasinya masalah yang dihadapi. Konseling merupakan pertemuan antara petugas konselor dengan TKI yang memungkinkan terjadinya dialog, sehingga TKI paham dan mampu memecahkan berbagai permasalahan yang dihadapi.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('84', '1', null, '240', 'Konselor TKI', 'Adalah tenaga ahli/profesional yang mempunyai kompetensi memberikan Konseling kepada TKI.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('85', '1', null, '126', 'Konvensi PBB ', 'Adalah pernyataan kesepakatan bersama terkait dengan nilai-nilai, norma, program dan rencana aksi dalam rangka menanggulangi masalah bersama yang dihasilkan dari suatu forum PBB. Forum PBB melibatkan sekelompok orang (dapat terdiri dari negarawan, usahawan, cendikiawan dan sebagainya), yang melakukan tukar-menukar informasi, pengalaman, diskusi dan hasilnya dituangkan dalam bentuk konvensi. Konvensi PBB yang penting antara lain yaitu konvensi PBB tahun 1990 tentang Perlindungan Hak-hak Buruh Migran dan anggota keluarganya.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('86', '1', null, '240', 'Korban Perdagangan Orang', 'Adalah seseorang yang mengalami penderitaan psikis, mental, fisik, seksual, ekonomi, dan atau sosial yang diakibatkan tindak pidana perdagangan orang.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('87', '1', null, '240', 'Kartu Perserta Asuransi TKI (KPA-TKI)', 'Kartu Perserta Asuransi TKI (KPA-TKI) adalah kartu yang diterbitkan oleh Penanggung (Konsorsium Asuransi) sebagai bukti keikutsertaan TKI dalam asuransi TKI yang merupakan bagian tak terpisahkan dari polis.', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 13:04:30', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('88', '1', null, '173', 'KPI', 'Adalah kesatuan Pelaut Indonesia, KPI adalah organisasi pelaut-pelaut Indonesia  dalam rangka menjaga komitmen persatuan dan solidaritas  serta memperjuangkan aspirasi dan kepentingan bersama. KPI berdiri pada tanggal 27 Desember 1945.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('89', '1', null, '240', 'Kredit TKI', 'Pinjaman TKI yang diperoleh dari perbankan untuk keperluan pembiayaan penempatan TKI untuk bekerja di luar negeri.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('90', '1', null, '173', 'Kartu Tenaga Kerja Luar Negeri (KTKLN)', 'Kartu Tenaga Kerja Luar Negeri (KTKLN) adalah kartu identitas bagi TKI yang memenuhi persyaratan dan prosedur untuk bekerja di luar negeri.', '4', '85', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 16:37:06', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('91', '1', null, '173', 'KUDLO', 'Kuwait Union of Domestic Labour Office adalah asosiasi agensi penyedia tenaga kerja rumah tangga di negara Kuwait.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('92', '1', null, '10', 'Labour Day ', 'Dimulai Hari Buruh ini pada 1 Mei 1886 yang menyatukan organisasi-organisasi buruh di Amerika Serikat untuk perjuangan yang bertujuan memperbaiki kondisi kerja yang lebih baik dengan memasukkan 8 jam kerja sehari, yang kemudian menjadi standard jam kerja di berbagai negara di dunia.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('93', '1', null, '240', 'Live Out Systems', 'Adalah sistem yang menggambarkan keberadaan tempat tinggal pekerja yang tidak satu rumah dengan majikan atau di gedung tempat kerjanya; hubungan kerja pekerja dengan penggguna sesuai standar 8 jam kerja per hari.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('94', '1', null, '240', 'Lowongan Kerja di luar negeri', 'Adalah lowongan kerja yang ada di negara-negara lain yang terjadi karena adanya permintaan Tenaga Kerja Indonesia (TKI) baik dari perusahaan berbadan hukum maupun pengguna perseorangan atau lowongan kerja yang ada di perusahaan berbadan hukum Indonesia dalam mengerjakan proyek maupun usahanya di luar negeri. ', '4', '381', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('95', '1', null, '173', 'LPK ', 'Lembaga Pelatihan Kerja (LPK) adalah badan hukum yang memiliki ijin dari Instansi Pemerintah untuk menyelenggarakan pelatihan kerja berbasis kompetensi.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('96', '1', null, '173', 'Lembaga Sertifikasi Profesi (LSP)', 'Lembaga Sertifikasi Profesi (LSP) adalah lembaga pelaksana kegiatan sertifikasi profesi yang memperoleh lisensi dari Badan Nasional Sertifikasi Profesi (BNSP). Lisensi ini diberikan melalui proses akreditasi oleh BNSP yang menyatakan bahwa LSP bersangkutan telah memenuhi syarat untuk melakukan kegiatan sertifikasi profesi. Lembaga independen yang berwenang menetapkan kualifikasi dan memberikan sertifikat kompetensi untuk menjamin mutu kompetensi dan kualifikasi pekerja secara professional untuk bekerja dalam suatu jabatan tertentu. Lembaga Sertifikasi Profesi dibentuk dengan keanggotaan dari unsur-unsur Organisasi Profesi, Organisasi Lembaga Pendidikan Profesi, Asosiasi Lembaga Profesi dan Birokrat. ', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 13:04:47', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('97', '1', null, '10', 'LTSP', 'Layanan Terpadu Satu Pintu adalah sistem pelayanan terpadu dalam rangka penempatan dan perlindungan TKI yang melibatkan berbagai instansi terkait : Pemerintah, Pemerintah Daerah, dan lembaga swasta/masyarakat terkait lainnya. Tujuan Pembentukan LTSP adalah dalam rangka memberikan pelayanan informasi, dokumen di satu tempat untuk terwujudnya sistem pelayanan yang cepat, mudah, murah, dan aman tanpa diskriminasi.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('98', '1', null, '173', 'Maktab Istiqdam', 'Adalah istilah untuk Mitra Usaha dari PPTKIS di luar negeri (Negara-negara Timur Tengah) yang mengurus penempatan, penyelesaian masalah dan perpanjangan kontrak TKI di negara tujuan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('99', '1', null, '126', 'Mandatory Consuler Notification', 'Pasal 36 konvensi Wina 1963 tentang hubungan konsuler (VCCR) menyebutkan bahwa petugas konsuler dari suatu perwakilan konsuler memiliki kebebasan berkomunikasi dengan warga negaranya serta memiliki akses terhadap mereka, demikian pula sebaliknya bahwa setiap warga Negara memilki kebebasan yang sama untuk berkomunikasi dan memiliki akses kepada petugas kounsuler negaranya. Apabila ia menghendaki, maka otoritas yang berwenang dari Negara penerima harus menginformasikan perwakilan konsuler Negara pengirim tanpa ditunda jika warga Negara dari Negara pengirim tersebut ditangkap atau ditahan atau dalam proses persidangan atau ditahan atas dasar lainnya (Consuler Notification). Sehubungan dengan hal tersebut, petugas konsuler berhak untuk mengunjungi warga negaranya yang berada di tahanan, untuk melakukan komunikasi atau korespondensi untuk mengatur pemberian batuan hukum (Consuler Assistance), namun petugas konsuler tidak dibenarkan bertindak untuk dan atas nama warga negaranya, jika warga negara tersebut tidak menginginkannya.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('100', '1', null, '173', 'Masa Penempatan', 'Adalah masa atau jangka waktu berlakunya perjanjian kerja yang disepakati TKI dan pengguna dan telah disetujui Perwakilan RI dan instansi ketenagakerjaan di negara tujuan.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('101', '1', null, '173', 'Mekanisme antar kerja luar negeri ', 'Adalah sistem pelayanan kepada pencari kerja luar negeri untuk memperoleh pekerjaan di luar negeri sesuai dengan bakat, minat dan kemampuannya dalam hubungan kerja. Atau pelayanan kepada pemberi kerja di luar negeri untuk memperoleh TKI sesuai dengan kebutuhannya serta kegiatan lain yang mendukung penempatan dan perlindungan TKI di luar negeri', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('102', '1', null, '10', 'Migran', 'Menurut dimensi waktu adalah orang yang berpindah ke tempat lain dengan tujuan untuk menetap dalam waktu 6 bulan atau lebih. Pekerja Migran adalah Orang yang bermigrasi dari wilayah kelahirannya ke tempat lain dan kemudian bekerja di tempat yang baru tersebut dalam jangka waktu relatif menetap. Pekerja migran mencakup sedikitnya dua tipe: pekerja migran internal dan pekerja migran internasional. Pekerja migran internal berkaitan dengan urbanisasi, sedangkan pekerja migran internasional tidak dapat dipisahkan dari globalisasi. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('103', '1', null, '10', 'Migran Sirkuler', 'Adalah orang yang berpindah tempat tetapi tidak termasuk menetap di tempat tujuan. migrant sirkuler biasanya adalah orang yang masih mempunyai keluarga atau ikatan dengan tempat asalnya seperti tukang becak, kuli bangunan, dan pengusaha warung tegal yang sehari-harinya mencari nafkah di kota dan pulang ke kampungnya setiap bulan atau beberpa bulan sekali.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('104', '1', null, '10', 'Migran Ulang-alik (Commuter)', 'Adalah orang yang pergi meninggalkan tempat tinggalnya secara teratur (misalnya setiap hari/minggu), pergi ke tempat lain untuk bekerja, berdagang, sekolah , atau untuk kegiatan lainnya, dan pulang ke tempat asalnya secara teratur pula. (misalnya pada sore, malam hari atau pada hari Minggu). Migran Ulang-alik biasanya menyebabkan jumlah penduduk di tempat tujuan lebih banyak pada waktu tertentu, misalnya pada siang hari.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('105', '1', null, '10', 'Migrasi', 'Adalah Perpindahan penduduk dengan tujuan untuk menetap dari satu tempat ke tempat lain melalui batas administrative (Migrasi Internal) atau batas politik- negara (Migrasi Internasional) dengan kata lain migrasi diartikan perpindahan yang relative permanen dari suatu daerah (Negara) ke daerah (Negara) lain. Migrasi Internasional adalah perpindahan penduduk dari suatu negara ke negara lain yang memuat dimensi ruang.<br />\r\n<br />\r\n<br />\r\n1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://id.wikipedia.org/wiki/Migrasi_manusia\">Apa yang dimaksud dengan migrasi ? </a>&nbsp;<br />\r\n2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://id.wikipedia.org/wiki/Imigrasi\">Apa yang dimaksud dengan imigrasi</a> ?<br />\r\n3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://data.worldbank.org/indicator/SM.POP.NETM\">Gambaran &nbsp;Net Migration Negara-negara Didunia?</a><br />\r\n4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://www.indexmundi.com/g/r.aspx?t=0&amp;v=65&amp;l=en\">GDP, perbandingan negara-negara di dunia?</a><br />\r\n5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://en.wikipedia.org/wiki/List_of_minimum_wages_by_country\">Upah minimum Negara-negara di Dunia ?</a><br />\r\n6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://en.wikipedia.org/wiki/List_of_countries_by_household_final_consumption_expenditure_per_capita\">Biaya hidup di beberapa Negara ?</a><br />\r\n7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://econ.worldbank.org/WBSITE/EXTERNAL/EXTDEC/EXTDECPROSPECTS/0,,contentMDK:22759429~pagePK:64165401~piPK:64165026~theSitePK:476883,00.html\">Berapa penerimaan remitansi Negara-negara di dunia?</a><br />\r\n8.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://www.thejakartapost.com/news/2013/04/29/ri-ranks-third-se-asia-remittance-list.html\">Penerimaan remitansi Indonesia diantara negara-negara di Asia Tenggara </a>?<br />\r\n9.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://www.ilo.org/dyn/normlex/en/f?p=NORMLEXPUB:12000:0::NO:::\">Daftar konvensi terkait buruh migrant&nbsp; ?</a><br />\r\n10.&nbsp;&nbsp;&nbsp; <a href=\"file:///G:/GLOSARI%202013/GLOSARI%2019112013.xls\">Daftar istilah </a>&nbsp;?<br />\r\n11.&nbsp;&nbsp;&nbsp; <a href=\"file:///J:/GOOGLE%20MINI/Glossary%202nd%20ed%20web.pdf\">Glossary on migration ?</a><br />\r\n', '4', '12', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-18 14:15:07', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('106', '1', null, '173', 'Mitra Usaha PPTKIS', 'Adalah instansi atau badan usaha berbentuk badan hukum di Negara tujuan yang bertanggung jawab menempatkan TKI pada pengguna. Dalam penempatan TKI, mitra usaha bekerjasama dengan PPTKIS didasarkan pada perjanjian kerjasama penempatan yang disetujui pemerintah negara setempat dan Perwakilan Republik Indonesia. Penilaian mitra usaha dan pengguna dilakukan oleh perwakilan RI, hasil penilaian  diumumkan secara periodik  setiap 3 bulan.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('107', '1', null, '126', 'MoU-TKI', 'Memorandum of Understanding adalah nota kesepahaman berkaitan dengan penempatan dan perlindungan TKI antara pemerintah RI dan Negara tujuan penempatan. ', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('108', '1', null, '173', 'Negara Tujuan Penempatan', 'Negara yang menjadi tujuan penempatan TKI adalah negara yang memberikan kesempatan/ peluang bagi TKI yang memenuhi persyaratan  untuk bekerja pada jabatan-jabatan tertentu dan proses penempatan dan perlindungannya tidak bertentangan dengan ketentuan-ketentuan yang ada dalam UU No.39 Tahun 2004. Setiap orang dilarang menempatkan calon TKI/TKI pada jabatan dan tempat pekerjaan yang bertentangan dengan nilai-nilai kemanusiaan dan norma kesusilaan serta peraturan perundang-undangan, baik di Indonesia maupun di negara tujuan atau di negara tujuan yang telah dinyatakan tertutup. ', '4', '16', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('109', '1', null, '240', 'Overstay TKI', 'Adalah kondisi yang menggambarkan status izin tinggal TKI di suatu negara yang telah melewati batas waktu yang diperkenankan berdasarkan Visa kerja yang dikeluarkan oleh negara tempatan.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('110', '1', null, '10', 'P4TKI', 'Pos Pelayanan Penempatan dan Perlindungan Tenaga Kerja Indonesia (P4TKI) adalah pos pelayanan di pelabuhan pemberangkatan (embarkasi) dan pemulangan (debarkasi) TKI yang dilengkapi dengan fasilitas yang memenuhi syarat atau di tempat-tempat yang dipandang perlu.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('111', '1', null, '240', 'Pahlawan Devisa', 'Julukan popular di masyarakat yang diberikan kepada TKI sebagai wujud penghargaan atas jerih payahnya bekerja di luar negeri dengan membawa valuta asing yang bermanfaat bagi bangsa dan negara.  Penghasilan  yang dibawa TKI dalam bentuk Valuta asing masuk ke Wilayah Negara Republik Indonesia berpengaruh terhadap ketersediaan cadangan Devisa Negara dan keseimbangan Neraca Pembayaran luar negeri.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('112', '1', null, '173', 'Pembekalan Akhir Pemberangkatan (PAP)', 'Pembekalan Akhir Pemberangkatan (PAP) adalah kegiatan pemberian pembekalan atau informasi kepada calon TKI yang akan berangkat bekerja ke luar negeri agar calon TKI Mempunyai kesiapan mental dan pengetahuan untuk bekerja di luar negeri,memahami hak dan kewajibannya serta dapat mengatasi masalah yang mungkin akan dihadapi.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 13:04:01', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('113', '1', null, '10', 'PAPA', 'Persatuan Agensi Pembantu Rumah Asing Malaysia (Malaysian Association of Foreign Maid Agencies)  adalah perkumpulan agensi yang mengurus tenaga kerja Indonesia yang bekerja sebagai pembantu rumah tangga di Malaysia.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('114', '1', null, '126', 'Pasar Kerja Luar Negeri', 'Adalah seluruh aktivitas atau kegiatan dari pelaku-pelaku di dalam negeri maupun luar negeri untuk mempertemukan pencari kerja di dalam negeri untuk mengisi lowongan yang ada di luar negeri.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('115', '1', null, '173', 'Paspor', 'Adalah dokumen resmi yang dikeluarkan oleh pejabat yang berwenang dari suatu negara yang memuat identitas pemegangnya dan berlaku untuk melakukan perjalanan antar negara. Paspor berisi biodata pemegangnya, yang meliputi antara lain foto pemegang, tanda tangan, tempat dan tanggal kelahiran, informasi kebangsaan dan terkadang juga informasi lain mengenai identitas individu. ', '4', '11', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('116', '1', null, '173', 'Pekerja Kilang', 'Julukan bagi pekerja warga negara asing yang bekerja sebagai buruh pabrik di Malaysia. Dalam bahasa Malaysia kilang artinya pabrik.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('117', '1', null, '240', 'Pelaku Perdagangan Orang', 'Adalah Orang Perseorangan atau korporasi yang melakukan tindak pidana perdagangan orang.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('118', '1', null, '173', 'Pelatihan Kerja TKI', 'Adalah keseluruhan kegiatan untuk memberikan, memperoleh, meningkatkan, serta mengembangkan kompetensi kerja, produktivitas, disiplin, sikap, dan etos kerja pada tingkat keterampilan dan keahlian tertentu sesuai dengan jenjang dan kualifikasi jabatan atau pekerjaan TKI.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('119', '1', null, '240', 'Pembelaan TKI', 'Adalah upaya hukum dalam rangka pemenuhan hak-hak CTKI / TKI sesuai dengan  peraturan perundang-undangan yang berlaku baik didalam negeri, di negara penempatan, serta hukum dan kebiasaan internasional.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('120', '1', null, '10', 'Pemerintah', 'Perangkat Negara Kesatuan Republik Indonesia yang terdiri dari presiden beserta para menteri, termasuk didalamnya  Badan Nasional Penempatan dan Perlindungan Tenaga Kerja Indonesia (BNP2TKI) dan Balai Pelayanan Penempatan dan Perlindungan Tenaga Kerja Indonesia (BP3TKI).', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('121', '1', null, '173', 'Penampungan TKI', 'Penampungan atau Akomodasi CTKI adalah tempat kegiatan menampung CTKI dalam rangka penyiapan penempatan TKI ke luar negeri yang berbentuk bangunan dengan memenuhi ketentuan peraturan perundangan yang berlaku.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('122', '1', null, '126', 'Pencari kerja luar negeri ', 'Adalah angkatan kerja yang sedang menganggur dan mencari kerja atau pekerjaan di luar negeri maupun yang sudah bekerja di luar negeri tetapi ingin pindah atau alih pekerjaan di luar negeri yang dinyatakan dengan aktifitasnya melamar pekerjaan kepada perusahaan jasa penempatan Tenaga Kerja Indonesia atau pengguna di luar negeri baik berupa perusahaan berbadan hukum tetap atau pengguna perseorangan.  ', '4', '11', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('123', '1', null, '173', 'Pendaftaran CTKI', 'Adalah pendaftaran pencari kerja yang telah mengikuti penyuluhan dalam rangka rekrut CTKI yang berminat bekerja di luar negeri yang selanjutnya mendaftarkan diri ke dinas kabupaten kota. Daftar CTKI Adalah daftar nama-nama Pencari kerja yang berminat bekerja ke luar negeri pada instansi pemerintah kabupaten/kota yang bertanggung jawab di bidang ketenagakerjaan.', '4', '112', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('124', '1', null, '173', 'Pendataan Calon TKI', 'Adalah bagian dari proses rekrutmen CTKI baik yang dilakukan oleh PPTKIS maupun oleh Dinas Tenaga Kerja setempat.', '4', '19', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('125', '1', null, '173', 'Penempatan TKI', 'Adalah kegiatan pelayanan untuk mempertemukan TKI sesuai dengan bakat, minat, dan kemampuannya dengan pemberi kerja di luar negeri yang meliputi keseluruhan proses perekrutan, pengurus dokumen, pendidikan dan pelatihan, penampungan, persiapan pemberangkatan, pemberangkatan sampai ke negara tujuan, dan pemulangan dari negara tujuan.&nbsp;(UU Nomor 39 tahun 2004)', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 11:46:15', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('126', '1', null, '173', 'Pengantar Kerja AKAN', 'Adalah Pegawai Negeri Sipil yang sudah memiliki sertifikat kursus Antar Kerja Antar Negara (AKAN) untuk melaksanakan proses rekrut CTKI melalui pelaksanaan sistem rekrut AKAN yang dimulai dengan kegiatan pendataan, penyuluhan, pendaftaran, wawancara, seleksi, dan penentuan nominatif yang disahkan oleh Pejabat Instansi Ketenagakerjaan Kabupaten/Kota.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('127', '1', null, '240', 'Pengawasan Ketenagakerjaan', 'Adalah kegiatan mengawasi dan menegakkan pelaksanaan peraturan perundang-undangan di bidang ketenagakerjaan. Petugas yang dapat melakukan pengawasan ketenagakerjaan  adalah pegawai negeri sipil (PNS) yang menjabat “pejabat fungsional pengawas ketenagakerjaan.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('128', '1', null, '240', 'Pengguna Bermasalah', 'Adalah Pengguna yang mengingkari kewajibannya sebagaimana tertuang dalam perjanjian kerja dengan TKI dan atau melanggar aturan serta norma-norma yang berlaku secara universal.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('129', '1', null, '173', 'Pengguna Jasa (User) TKI', 'Adalah instansi pemerintah, atau badan usaha yang berbadan hukum atau perseorangan di luar negeri yang mempekerjakan TKI. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 12:52:22', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('130', '1', null, '173', 'Pengguna Perseorangan', 'Adalah orang-perseorangan yang mempekerjakan TKI pada pekerjaan-pekerjaan antara lain sebagai penata laksana rumah tangga, pengasuh bayi, atau perawat manusia lanjut usia, supir, tukang kebun/taman. Pekerjaan-pekerjaan tersebut biasa disebut sebagai pekerjaan di sektor informal. Penempatan TKI pada pengguna perseorangan harus melalui mitra usaha di negara tujuan. ', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('131', '1', null, '173', 'Penghentian Penempatan TKI ', 'Penghentian dan/atau pelarangan penempatan TKI di luar negeri untuk negara tertentu atau penempatan TKI pada jabatan-jabatan tertentu di luar negeri dapat dilakukan oleh pemerintah dengan pertimbangan untuk melindungi calon TKI/TKI, pemerataan kesempatan kerja dan/atau untuk kepentingan ketersediaan tenaga kerja sesuai dengan kebutuhan nasional. Dalam menghentikan dan/atau melarang penempatan TKI, pemerintah memperhatikan saran dan pertimbangan Badan Nasional Penempatan dan Perlindungan TKI (BNP2TKI).', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('132', '1', null, '240', 'Pengiriman TKI ilegal', 'Adalah Tindakan memberangkatkan atau melabuhkan seseorang dari satu tempat ke tempat lain.', '4', '11', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('133', '1', null, '240', 'Penjeratan Utang terhadap TKI', 'Adalah perbuatan menempatkan orang dalam status atau keadaan menjaminkan atau terpaksa menjaminkan dirinya atau keluarganya atau orang-orang yang menjadi tanggungjawab, atau jasa pribadinya sebagai bentuk pelunasan utang.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('134', '1', null, '173', 'Penyedian TKI', 'Adalah penyediaan yang mengacu tidak hanya pada besarnya angkatan kerja tetapi juga kepada keahlian dan lokasi geografisnya, dan juga kesediaan kemampuannya untuk menjadi produktif jika bekerja di luar negeri.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('135', '1', null, '173', 'Penyerahan Daftar Nominatif CTKI ', 'Adalah penyerahan daftar nominatif CTKI yang diseleksi melalui mekanisme Antar Kerja Antar Negara (AKAN) yang dilaksanakan oleh Pegawai Pengantar Kerja. Penyerahan daftar nominatif CTKI tersebut dilakukan secara tertulis dengan berita acara serah terima CTKI oleh Kepala Dinas Tenaga Kerja dan atau yang ditunjuk atas nama Kepala Dinas Tenaga Kerja Kab/Kota. ', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('136', '1', null, '240', 'Perdagangan Orang ', 'Adalah tindakan perekrutan, pengangkutan, penampungan, pengiriman, pemindahan, atau penerimaan seseorang dengan ancaman kekerasan, penggunaan kekerasan, penculikan, penyekapan, pemalsuan, penipuan, penyalahgunaan kekuasaan atau memberi bayaran atau manfaat, sehingga memperoleh persetujuan dari orang yang memegang kendali atas orang lain tersebut,baik yang dilakukan di dalam negara maupun antar negara, untuk tujuan eksploitasi atau mengakibatkan orang tereksploitasi.<br />\n<ol>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/475\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Apa itu TKI ilegal?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/474\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Mengapa bisa terjadi TKI ilegal?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/473\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Apa Resiko Menjadi TKI Ilegal ?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/472\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Apa Itu Trafficking ?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/471\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Mengapa Bisa Terjadi Trafficking?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/470\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Seperti Apa Tindakan Trafficking?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/469\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); transition: all 0.5s; -webkit-transition: all 0.5s;\">Bagaimana Menjadi TKI yang aman?</a></li>\n</ol>\n', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 18:41:47', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('137', '1', null, '240', 'Perekrutan TKI IIegal', 'Adalah Tindakan yang meliputi mengajak, mengumpulkan, membawa, atau memisahkan seseorang dari keluarga atau komunitasnya.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('138', '1', null, '173', 'Perjanjian Kerja Sama Penempatan', 'Adalah perjanjian tertulis antara pelaksana penempatan TKI swasta dengan mitra usaha atau pengguna yang memuat hak dan kewajiban masing-masing pihak dalam rangka penempatan serta perlindungan TKI di negara tujuan. ', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('139', '1', null, '240', 'Perjanjian Kerja TKI', 'Adalah perjanjian tertulis antara TKI dengan pengguna yang memuat syarat-syarat kerja, hak dan kewajiban masing-masing pihak. Perjanjian kerja dibuat untuk jangka waktu paling lama 2 (dua) tahun dan dapat diperpanjang untuk jangka waktu paling lama 2 (dua) tahun. Dikecualikan dari ketentuan jangka waktu perjanjian untuk jabatan atau jenis pekerjaan tertentu. Ketentuan mengenai jabatan atau jenis pekerjaan tertentu yang dikecualikan dari jangka waktu perjanjian kerja, diatur dengan Peraturan Menteri. Perpanjangan jangka waktu perjanjian kerja dapat dilakukan oleh TKI yang bersangkutan atau melalui pelaksana penempatan TKI swasta. Perpanjangan harus disepakati oleh para pihak sekurang-kurangnya 3 (tiga) bulan sebelum perjanjian kerja pertama berakhir. Perjanjian kerja dan jangka waktu perpanjangan perjanjian kerja wajib mendapat persetujuan dari pejabat berwenang pada Perwakilan Republik Indonesia di negara tujuan. Pengurusan untuk mendapatkan persetujuan dilaksanakan oleh dan menjadi tanggung jawab pelaksana penempatan TKI swasta. Ketentuan mengenai persyaratan dan tata cara memperoleh persetujuan perjanjian kerja dan perpanjangan jangka waktu perjanjian kerja diatur dengan Peraturan Menteri. TKI yang bekerja pada pengguna perseorangan yang telah berakhir perjanjian kerjanya dan akan memperpanjang perjanjian kerja TKI yang bersangkutan harus pulang terlebih dahulu ke Indonesia. Dalam hal perpanjangan dilakukan sendiri oleh TKI yang bersangkutan, maka pelaksana penempatan TKI swasta tidak bertanggungjawab atas risiko yang menimpa TKI dalam masa perpanjangan perjanjian kerja. Bagi TKI yang bekerja pada pengguna perseorangan, apabila selama masa berlakunya perjanjian kerja terjadi perubahan jabatan atau jenis pekerjaan, atau pindah pengguna, maka perwakilan pelaksana penempatan TKI swasta wajib mengurus perubahan perjanjian kerja dengan membuat perjanjian kerja baru dan melaporkan kepada Perwakilan Republik Indonesia.', '4', '18', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('140', '1', null, '240', 'Perjanjian Penempatan TKI', 'Perjanjian penempatan TKI adalah perjanjian tertulis antara pelaksana penempatan TKI swasta dengan calon TKI yang memuat hak dan kewajiban masing-masing pihak dalam rangka penempatan TKI di negara tujuan sesuai dengan peraturan perundang-undangan. Pelaksana penempatan TKI swasta membuat dan menandatangani perjanjian penempatan dengan pencari kerja yang telah dinyatakan memenuhi persyaratan administrasi dalam proses perekrutan. Perjanjian penempatan diketahui oleh instansi yang bertanggungjawab di bidang ketenagakerjaan kabupaten/kota. Untuk dapat ditempatkan di luar negeri, calon TKI harus memiliki dokumen perjanjian penempatan. Perjanjian penempatan TKI dibuat secara tertulis dan ditandatangani oleh calon TKI dan pelaksana penempatan TKI swasta setelah calon TKI yang bersangkutan terpilih dalam perekrutan.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('141', '1', null, '240', 'Perlindungan TKI', 'Adalah segala upaya untuk melindungi kepentingan CTKI/TKI dalam mewujudkan terjaminnya pemenuhan hak-haknya sesuai dengan peraturan perundang-undangan, baik sebelum, selama, maupun sesudah bekerja. Dalam rangka pemberian perlindungan selama masa penempatan TKI di luar negeri, Perwakilan Republik Indonesia melakukan pembinaan dan pengawasan terhadap perwakilan pelaksana penempatan TKI swasta dan TKI yang ditempatkan di luar negeri. Sedangkan perlindungan di dalam negeri dilaksanakan oleh instansi yang bertanggung jawab di bidang ketenagakerjaan, pemerintah, pemerintah provinsi dan pemerintah kabupaten/kota.&nbsp;(UU Nomor 39 tahun 2004)', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 11:49:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('142', '1', null, '10', 'Perpres 81 / 2006', 'Perpres 81 / 2006 Adalah singkatan yang umum digunakan dalam penyebutan Peraturan Presiden Republik Indonesia No 81 Tahun 2006 Tentang Badan Nasional Penempatan Dan Perlindungan Tenaga Kerja Indonesia (BNP2TKI).', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('143', '1', null, '173', 'Pertemuan permintaan dan penawaran TKI ', 'Adalah pertemuan antara permintaan dan penawaran TKI dengan pengguna di luar negeri. Pasara kerja tidak hanya terbatas pada kesempatan kerja dalam hubungan kerja seperti pekerjaan mandiri, pekerjaan untuk memperoleh laba atau pendapatan di negara lain.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('144', '1', null, '173', 'Perwakilan PPTKIS di luar negeri', 'Adalah Perwakilan PPTKIS yang berada di luar negeri yang selanjutnya disebut PERWALU merupakan badan hukum atau perseorangan yang bertindak untuk atas nama PPTKIS di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('145', '1', null, '10', 'Perwakilan RI', 'Adalah Perwakilan pemerintah Indonesia di negara yang akan menjadi tujuan TKI seperti Kedutaan Besar Republik Indonesia (KBRI) dan/atau Konsulat Jenderal Republik Indonesia (KJRI).', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('146', '1', null, '240', 'Pesangon', 'Adalah pemberian berupa uang atau barang dari pengusaha kepada pekerjanya sebagai akibat adanya Pemutusan Hubungan Kerja (PHK) sesuai dengan peraturan perundangan yang berlaku.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('147', '1', null, '173', 'Pewawancara', 'Adalah pegawai pengantar kerja yang sudah mendapatkan pendidikan dan latihan pengantar kerja untuk melakukan kegiatan wawancara  kepada pencari kerja luar negeri.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('148', '1', null, '240', 'PHK-TKI ', 'Adalah pengakhiran hubungan kerja TKI dan pengguna di luar negeri karena suatu hal tertentu yang mengakibatkan berakhirnya hak dan kewajiban yang melekat secara hukum pada masing-masing pihak yang mengikatkan diri dalam perjanjian kerja.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('149', '1', null, '173', 'PLRT', 'Penata Laksana Rumah Tangga (PLRT) adalah salah satu jenis jabatan TKI yang khusus mengerjakan pekerjaan-pekerjaan rumah tangga pada pengguna perseorangan di luar negeri.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('150', '1', null, '240', 'Polis Asuransi', 'Adalah suatu akta yang berisi suatu asuransi/pertanggungan yang dibuat secara tertulis yang diterbitkan oleh perusahaan asuransi atau underwriternya (perusahaan yang menjamin perusahaan asuransi yang bersangkutan).', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('151', '1', null, '240', 'PPNS-TKI', 'Adalah Pegawai Negeri Sipil (PNS) BNP2TKI yang menjabat sebagai “Penyidik pegawai negeri sipil” yang mempunyai wewenang melakukan pemeriksaan atas kebenaran laporan tentang tindak pidana di bidang penempatan dan perlindungan TKI; melakukan pemeriksaan terhadap orang yang diduga melakukan tindak pidana di bidang penempatan dan perlindungan TKI; meminta keterangan dan bahan bukti dari orang atau badan hukum sehubungan dengan tindak pidana di bidang penempatan dan perlindungan TKI; melakukan pemeriksaan atau penyitaan bahan atau barang bukti dalam perkara tindak pidana di bidang penempatan dan perlindungan TKI; melakukan pemeriksaan atas surat dan/atau dokumen lain tentang tindak pidana di bidang penempatan dan perlindungan TKI; meminta bantuan tenaga ahli dalam rangka pelaksanaan tugas penyidikan tindak pidana di bidang penempatan dan perlindungan TKI; menghentikan penyidikan apabila tidak terdapat cukup bukti yang membuktikan tentang adanya tindak pidana di bidang penempatan dan perlindungan TKI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('152', '1', null, '173', 'Pelaksana Penempatan Tenaga Kerja Indonesia Swasta (PPTKIS)', 'Pelaksana Penempatan Tenaga Kerja Indonesia Swasta (PPTKIS) adalah Badan Hukum yang telah memperoleh izin tertulis dari pemerintah untuk menyelenggarakan pelayanan penempatan TKI di luar negeri.&nbsp;(UU Nomor 39 tahun 2004)', '4', '60', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 12:51:07', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('153', '1', null, '173', 'Pra-penempatan TKI', 'Adalah masa penyiapan dimana TKI melakukan upaya-upaya memenuhi ketentuan/kelengkapan yang dipersyaratkan antara lain dokumen keberangkatan, kompetensi, kesehatan dan asuransi  untuk dapat ditempatkan di luar negeri.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('154', '1', null, '173', 'Preliminary Training', 'Adalah pembekalan sebagai salah satu syarat bagi TKI yang akan ditempatkan bekerja di luar negeri. Istilah Preliminary Training umumnya digunakan bagi kegiatan pembekalan untuk penempatan TKI program G to G ke Korea Selatan.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('155', '1', null, '173', 'Program G to G', 'Program Government to Government adalah program kerjasama penempatan dan perlindungan TKI antara pemerintah Indonesia dengan pemerintah negara lain. Dalam program ini kegiatan pelayanan untuk mempertemukan TKI yang meliputi keseluruhan proses perekrutan, pengurusan dokumen, pendidikan dan pelatihan, persiapan pemberangkatan, pemberangkatan sampai ke negara tujuan, dan pemulangan dari negara tujuan di fasilitasi oleh pemerintah Indonesia dan pemerintah negara tujuan.<br />\n<br />\n<a href=\"http://www.bnp2tki.go.id/prosedur-mainmenu-228/402-prosedur-penempatan-tenaga-kerja-indonesia-ke-korea-program-g-to-g.html\" target=\"_blank\"><b>Program G to G ke Korea</b></a><br />\n<a href=\"http://www.bnp2tki.go.id/prosedur-mainmenu-228/43-syarat-syarat-penempatan-tenaga-kerja-indonesia-ke-korea.html\" target=\"_blank\">Syarat-syarat Penempatan Tenaga Kerja Indonesia ke Korea</a><br />\n<div>\n	<a href=\"http://www.bnp2tki.go.id/informasi-ke-korea-mainmenu-227/8344-pelatihan-gratis-diperkerjakan-di-perusahaan-korea.html\" target=\"_blank\">Pelatihan Gratis Diperkerjakan di Perusahaan Korea (17 Mei 2013)</a></div>\n<div>\n	<a href=\"http://www.bnp2tki.go.id/informasi-ke-korea-mainmenu-227/9580-pendaftaran-ujian-khusus-eps-topik-cbt-ke-2-2014.html\" target=\"_blank\">Pendaftaran Ujian Khusus EPS-TOPIK CBT ke 2 (2014)</a><br />\n	<a href=\"http://www.bnp2tki.go.id/sending-mainmenu-177.html\" target=\"_blank\">Sending</a><br />\n	<a href=\"http://www.bnp2tki.go.id/slc-mainmenu-180.html\" target=\"_blank\">SLC (Standard Labour Contact)</a><br />\n	<a href=\"http://www.bnp2tki.go.id/informasi-ke-korea-mainmenu-227.html\" target=\"_blank\">Preliminary Training dan Info</a><br />\n	<a href=\"http://www.bnp2tki.go.id/informasi-ke-korea-mainmenu-227.html\" target=\"_blank\">P</a><a href=\"http://www.bnp2tki.go.id/prosedur-mainmenu-220.html\">rogram G to G ke Jepang</a><br />\n	<a href=\"http://www.bnp2tki.go.id/formulir-mainmenu-216.html\" target=\"_blank\">Formulir</a><br />\n	<a href=\"http://www.bnp2tki.go.id/biaya-mainmenu-217/246--bnp2tki-membuka-lowongan-kerja-ke-jepang.html\" target=\"_blank\">Info Lowongan dan Biaya</a><br />\n	<a href=\"http://www.bnp2tki.go.id/interview-dan-aptitude-test-mainmenu-218.html\" target=\"_blank\">Interview dan Aptitude Test</a></div>\n', '4', '85', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-22 17:38:30', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('156', '1', null, '173', 'Program G to P', 'Program Goverment to Private adalah program kerjasama penempatan dan perlindungan TKI antara pemerintah Indonesia dengan swasta berbadan hukum di negara tujuan. Dalam program ini kegiatan pelayanan untuk mempertemukan TKI yang meliputi keseluruhan proses perekrutan, pengurusan dokumen, pendidikan dan pelatihan, persiapan pemberangkatan, pemberangkatan sampai ke negara tujuan, dan pemulangan dari negara tujuan di fasilitasi oleh pemerintah Indonesia dan swasta berbadan hukum di negara tujuan.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('157', '1', null, '173', 'Program P to P', 'Adalah program kerjasama penempatan dan perlindungan TKI antara PPTKIS dengan mitra usaha/pengguna di negara tujuan (swasta dengan swasta atau disebut Private to Private atau P to P) dan merupakan kegiatan pelayanan untuk mempertemukan TKI yang meliputi keseluruhan proses perekrutan, pengurusan dokumen, pendidikan dan pelatihan, persiapan pemberangkatan, pemberangkatan sampai ke negara tujuan, dan pemulangan dari negara tujuan di fasilitasi oleh PPTKIS, mitra usaha/pengguna, pemerintah Indonesia dan pemerintah negara tujuan.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('158', '1', null, '173', 'Psikotes TKI', 'Adalah suatu ujian yang diadakan terhadap pelamar atau CTKI untuk mengetahui bakat, minat atau sikapnya.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('159', '1', null, '240', 'Purna Penempatan TKI', 'Adalah masa dimana perjanjian kerja antara TKI dan pengguna telah berakhir dan TKI kembali ke tanah air. Pengakhiran masa kerja dapat disebabkan karena kontrak kerja telah berakhir, pemutusan hubungan kerja sebelum masa perjanjian kerja berakhir, terjadi perang, bencana alam, atau wabah penyakit di negara tujuan, mengalami kecelakaan kerja yang mengakibatkan tidak bisa menjalankan pekerjaannya lagi, meninggal dunia di negara tujuan, cuti, dideportasi oleh pemerintah setempat.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('160', '1', null, '173', 'Recycling TKI', 'Adalah penempatan kembali TKI bermasalah ke suatu negara penempatan, dimana TKI tersebut langsung diberangkatkan ke negara tujuan baru  tanpa terlebih dahulu kembali ke daerah asalnya. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('161', '1', null, '173', 'Re-entry Permit TKI', 'Adalah status yang dipunyai oleh TKI untuk masuk dan tinggal kembali di suatu negara dimana izin tinggalnya belum habis. Re-entry permit terbagi menjadi dua yaitu Izin satu kali, dapat digunakan satu kali atau beberapa kali.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('162', '1', null, '173', 'Rekomendasi Paspor TKI', 'Adalah Surat rekomendasi yang dikeluarkan oleh instansi pemerintah di kabupaten/kota yang membidangi ketenagakerjaan untuk membuat paspor bagi TKI yang akan bekerja di luar negeri. ', '4', '43', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('163', '1', null, '173', 'Rekrut CTKI', 'Adalah Proses pencarian atau pengumpulan calon TKI didahului dengan memberikan informasi kepada calon TKI sekurangkurangnya tentang: tata cara perekrutan; dokumen yang diperlukan; hak dan kewajiban calon TKI/TKI; situasi, kondisi, dan resiko di negara tujuan; dan tata cara perlindungan bagi TKI. Perekrutan dilakukan oleh pelaksana penempatan TKI swasta dan pencari kerja yang terdaftar pada instansi pemerintah kabupaten/kota yang bertanggung jawab di bidang ketenagakerjaan. ', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('164', '1', null, '173', 'Rekrutmen Fee TKI', 'Adalah ongkos atau biaya perekrutan calon TKI  antara lain untuk mengurus dokumen jati diri, keberangkatan, pelatihan kerja dan sertifikasi kompetensi kerja, pemeriksaan kesehatan dan psikologi, asuransi dan lain-lain, biaya yang diperlukan untuk keperluan penempatan di luar negeri. Untuk beberapa negara Recrutment fee itu dibayar oleh pengguna melalui Mitra Usaha.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('165', '1', null, '240', 'Remitansi', 'Adalah hasil kerja TKI di luar negeri yang sudah dikonversikan ke dalam nilai Rupiah. ', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('166', '1', null, '173', 'RPTKI', 'Adalah Rencana Penempatan TKI yang disiapkan oleh PPTKIS yang menggambarkan kegiatan promosi dan pemasaran, kegiatan penyediaan CTKI, jumlah TKI yang akan di tempatkan, jenis pekerjaan atau jabatan pada negara tujuan penempatan, penyiapan kualitas tki dan pengelolaan perlindungan TKI di luar negeri dalam jangka waktu yang ditetapkan (3 tahun).', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('167', '1', null, '126', 'Sanarcom', 'Adalah singkatan dari Saudi National Recruitment Committee yang merupakan asosiasi perusahaan jasa tenaga kerja asing di Saudi Arabia. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('168', '1', null, '173', 'SARKES', 'Adalah singkatan dari Sarana Kesehatan yang menyediakan jasa pelayanan pemeriksaan kesehatan bagi calon TKI.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('169', '1', null, '173', 'Seafarer', 'Adalah tenaga Kerja  pelaut yang memiliki sertifikat  sebagai tenaga kerja pelaut yang berlayar di laut.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('170', '1', null, '173', 'Seafarer Indonesia', 'Adalah TKI  pelaut yang sudah memiliki sertifikat pelaut antara lain sebagai Nakhoda, kelasi dan Anak Buah Kapal (ABK) dan wajib memiliki Kartu Tenaga Kerja Luar Negeri (KTKLN)  yang berlayar di laut.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('171', '1', null, '173', 'Seleksi TKI', 'Adalah proses penentuan pelamar untuk bekerja di Luar negeri yang sesuai dari jumlah pelamar untuk menduduki jabatan yang lowong di luar negeri. Usaha-usaha yang dilandaskan pada taksiran untuk pemenuhan kebutuhan permintaan TKI di luar negeri secara tepat guna, sesuai   persyaratan teknis, psikologis maupun administratif.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('172', '1', null, '173', 'Sending Data CTKI', 'Adalah pengiriman data dokumen calon TKI yang telah memenuhi  persyaratan yang ditetapkan untuk bekerja di Korea Selatan. Pengguna berdasarkan data tersebut, selanjutnya akan memilih CTKI yang sesuai dengan kebutuhannya. ', '4', '11', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('173', '1', null, '10', 'Serikat Pekerja', 'Adalah organisasi yang dibentuk dari, oleh, dan untuk pekerja/buruh baik di perusahaan maupun di luar perusahaan, yang bersifat bebas, terbuka, mandiri, demokratis, dan bertanggung jawab guna memperjuangkan, membela serta melindungi hak dan kepentingan pekerja/buruh serta meningkatkan kesejahteraan pekerja/buruh dan keluarganya. ', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('174', '1', null, '173', 'Sertifikasi Kompetensi  Kerja', 'Adalah proses pemberian sertifikat kompetensi yang dilakukan secara sistematis dan objektif melalui uji kompetensi yang mengacu kepada Standar Kompetensi Kerja Nasional Indonesia (SKKNI) dan/atau internasional.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('175', '1', null, '173', 'Sertifikat Kesehatan', 'Adalah surat keterangan sehat untuk bekerja (fit to work) yang dikeluarkan oleh sarana kesehatan yang menyatakan kesehatan fisik dan jiwa Calon TKI/TKI setelah dilakukan pemeriksaan kesehatan dan ditandatangani oleh dokter penanggungjawab pemeriksa.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 13:26:36', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('176', '1', null, '173', 'Sertifikat Kompetensi', 'Adalah surat keterangan yang menerangkan bahwa calon TKI memiliki keterampilan kerja untuk bekerja suatu jabatan tertentu.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('177', '1', null, '173', 'Sertifikat Pelatihan', 'Adalah surat keterangan yang menerangkan bahwa calon TKI telah mengikuti pelatihan kompetensi di BLK-LN untuk bekerja pada suatu jabatan tertentu.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('178', '1', null, '240', 'Shelter TKI', 'Adalah tempat penampungan sementara TKI bermasalah dalam masa menunggu proses penyelesaian masalahnya oleh Kantor Perwakilan RI di negara penempatan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('179', '1', null, '173', 'SIP', 'Surat Izin Pengerahan yang selanjutnya disingkat SIP adalah izin yang diberikan pemerintah kepada pelaksana penempatan TKI Swasta(PPTKIS)untuk merekrut calon TKI dari daerah tertentu untuk jabatan tertentu,dan untuk bekerja pada calon pengguna tertentu untuk jangka waktu tertentu.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('180', '1', null, '173', 'SIPP-TKI', 'Surat Izin Pelaksana Penempatan TKI (SIPP-TKI) adalah izin tertulis yang diberikan oleh menteri kepada perusahaan yang akan menjadi Pelaksana Penempatan TKI Swasta (PPTKIS).', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('181', '1', null, '173', 'Sisko TKLN', 'Adalah Sistem Komputerisasi Terpadu Pelayanan Penempatan TKI di Luar Negeri yang menggunakan sistem aplikasi guna memfasilitasi pelayanan pembuatan dokumen tenaga kerja Indonesia ke  luar negeri. Dengan database TKI yang lengkap dalam Sisko TKLN, keberadaan Perusahaan Pengirim Tenaga Kerja Indonesia Swasta (PPTKIS) bisa dipantau (monitoring) setiap saat dan mempermudah proses pengurusan dokumen pemberangkatan TKI ke luar negeri.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('182', '1', null, '240', 'Skorsing', 'Adalah penghentian sementara sebagian atau seluruh kegiatan usaha penempatan TKI  merupakan sanksi administrasi yang dijatuhkan kepada PPTKIS oleh Direktur Jenderal dalam rangka pembinaan terhadap PPTKIS agar taat dalam melaksanakan ketentuan penempatan dan perlindungan TKI di luar negeri sebagaimana diatur dalam undang-undang N0. 39 Tahun 2004', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('183', '1', null, '173', 'SLC', 'Adalah Standard Labour Contract  SLC yang merupakan konsep perjanjian kerja yang dsiapkan oleh pihak HRD KOREA bagi CTKI yang memenuhi persyaratan yang ditetapkan calon pengguna di Korea. ', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('184', '1', null, '126', 'Spesifikasi lowongan pekerjaan ', 'Adalah uraian persyaratan kualitas minimum seseorang bisa diterima guna melaksanakan tugas-tugas dalam jabatan tertentu dengan baik dan kompoten, menggambarkan ringkasan pekerjaan secara umum, jelas dan pasti, menggambarkan uraian informasi tingkat pendidikan yang dipersyaratkan berdasarkan spesifikasi pekerjaan. Manfaat spesifikasi lowongan pekerjaan adalah menjadi dasar untuk melaksanakan rekrut dan seleksi guna memilih orang yang kompoten sesuai dengan kebutuhan.', '4', '82', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('185', '1', null, '240', 'BPK-TKI', 'Balai Pelayanan Kepulangan TKI (SPK-TKI) adalah satuan tugas pelayanan yang memfasilitasi kepulangan TKI dari bandara Soekarno-Hatta ke daerah asal TKI. SPK-TKI beroperasi di Gedung Pendataan Kepulangan TKI (GPK-TKI) Selapajang, Tangerang.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('186', '1', null, '10', 'SPLP ', 'Adalah Surat Perjalanan Laksana Pasport atauTravel Document In Lieu of Passpor merupakan surat yang dikeluarkan oleh Perwakilan RI (KBRI/KJRI/KRI) di luar negeri  sebagai pengganti paspor.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('187', '1', null, '173', 'Surat Pengantar Rekrut', 'Adalah  surat pengantar yang dikeluarkan oleh BNP2TKI/BP3TKI yang ditujukan kepada instansi ketenagakerjaan kabupaten/kota yang menjelaskan rencana perekrutan CTKI oleh Pelaksana Penempatan Tenaga Kerja Indonesia Swasta (PPTKIS) surat pengantar rekrut selanjutnya dijadikan dasar instansi ketenagakerjaan kabupaten/kota untuk menerbitkan izin rekrut.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('188', '1', null, '240', 'Tarhil', 'Adalah tempat penahanan sementara bagi TKI yang dianggap melanggar ketentuan peraturan keimigrasian sampai pihak imigrasi  Arab Saudi memberikan visa sehingga yang bersangkutan bisa bebas berbaur dengan komunitas setempat atau dipulangkan ke negara asalnya.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('189', '1', null, '173', 'Tenaga kerja kontrak', 'Adalah tenaga kerja yang bekerja pada pengusaha untuk melakukan pekerjaan tertentu dengan menerima upah yang didasarkan atas kesepakatan untuk hubungan kerja waktu tertentu, dan/atau selesainya pekerjaan tertentu. Tenaga kerja kontrak sering disebut juga tenaga outsourcing.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('190', '1', null, '240', 'Tindak pidana Perdagangan orang', 'Adalah setiap tindakan atau serangkaian tindakan yang memenuhi unsur-unsur tindak pidana yang ditentukan dalam undang-undang Republik Indonesia No. 21 tahun 2007 tentang Pemberantasan Tindak Pidana Perdagangan Orang.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('191', '1', null, '173', 'Tenaga Kerja Indonesia (TKI)', 'Tenaga Kerja Indonesia yang selanjutnya disebut dengan TKI adalah setiap warga negara Indonesia yang memenuhi syarat untuk bekerja di luar negeri dalam hubungan kerja untuk jangka waktu tertentu dengan menerima upah.&nbsp;(UU Nomor 39 tahun 2004)', '4', '52', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 16:27:24', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('192', '1', null, '240', 'TKI Bermasalah', 'Adalah Tenaga Kerja Indonesia yang mengalami permasalahan dengan pengguna berkaitan adanya pengingkaran hak dan/atau kewajiban pada Perjanjian Kerja, mengalami kecelakaan kerja, melakukan atau menjadi korban tindak kriminal di negara penempatan.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('193', '1', null, '173', 'TKI Formal', 'AdalahTenaga Kerja Indonesia yang bekerja pada pengguna berbadan hukum tetap. ', '4', '22', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('194', '1', null, '240', 'TKI Ilegal', 'Adalah Tenaga Kerja Indonesia yang bekerja di luar negeri yang melanggar ketentuan peraturan perundang-undangan yang berlaku di dalam negeri maupun di negara tujuan.<br />\n<div>\n	&nbsp;</div>\n<ol>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/475\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Apa itu TKI ilegal?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/474\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Mengapa bisa terjadi TKI ilegal?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/473\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Apa Resiko Menjadi TKI Ilegal ?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/472\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Apa Itu Trafficking ?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/471\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Mengapa Bisa Terjadi Trafficking?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/470\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Seperti Apa Tindakan Trafficking?</a></li>\n	<li>\n		<a href=\"http://info.bnp2tki.go.id/home/info_detail/469\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: \'Open Sans\', sans-serif; font-size: 13px; line-height: 19.440000534057617px; vertical-align: baseline; text-decoration: none; color: rgb(68, 133, 246); -webkit-transition: all 0.5s; transition: all 0.5s;\">Bagaimana Menjadi TKI yang aman?</a></li>\n</ol>\n', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 18:43:37', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('195', '1', null, '173', 'TKI Informal', 'Adalah Tenaga Kerja Indonesia yang bekerja pada pengguna perserongan.', '4', '9', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('196', '1', null, '173', 'TKI Mandiri', 'Adalah Tenaga Kerja Indonesia yang bekerja di luar negeri, tidak difasilitasi oleh lembaga pemerintah/swasta dan TKI berhubungan langsung dengan Pengguna. Dalam proses penempatan TKI Mandiri, TKI wajib melapor kepada instansi yang bertanggung jawab di bidang ketenagakerjaaan, perwakilan RI dan memiliki Kartu Tenaga Kerja Luar Negeri (KTKLN).', '4', '32', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', '2014-06-25 16:36:21', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', null, null);
INSERT INTO `h_info_tki` VALUES ('197', '1', null, '173', 'TKI profesional ', 'Adalah Tenaga Kerja Indonesia profesional yang bekerja secara profesional di bidang atau jabatan tertentu didasarkan pada pengetahuan dan pengalaman profesional yang dimiliki.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('198', '1', null, '173', 'TKI Prosedural', 'Adalah Tenaga Kerja Indonesia yang berangkat dan bekerja di luar negeri sesuai dengan ketentuan yang berlaku.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('199', '1', null, '173', 'TKI terampil', 'Adalah TKI yang memperoleh kualifikasi penuh yang diperlukan atau memenuhi syarat untuk melaksanakan pekerjaan manual atau teknik tertentu pada pengguna di luar negeri.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('200', '1', null, '173', 'TKW', 'Adalah sebutan bagi wanita Indonesia yang bekerja di luar negeri pada pengguna perseorangan. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('201', '1', null, '173', 'Uji kompetensi CTKI', 'Adalah kegiatan untuk mengukur tingkat kemapuan calon TKI baik teori maupun praktek sesuai dengan standar kualifikasi kompetensi yang ditetapkan. Uji kompetensi CTKI dilaksanakan oleh lembaga uji kompetensi.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('202', '1', null, '240', 'Underpayment', 'Adalah kondisi pengupahan dimana gaji/upah yang di terima oleh TKI dibawah standard upah minimum negara penempatan atau dibawah gaji/upah yang tertera dalam  Perjanjian Kerja. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('203', '1', null, '10', 'UP3TKI- Surabaya', 'Unit Pelayanan, Penempatan dan Perlindungan TKI (UP3TKI) Surabaya adalah Unit Pelaksana Teknis Daerah (UPT-D) di lingkungan Pemerintah Daerah Provinsi Jawa Timur yang mempunyai tugas memberikan kemudahan pelayanan pemprosesan seluruh dokumen penempatan, perlindungan dan penyelesaian masalah tenaga kerja Indonesia.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('204', '1', null, '173', 'Upah TKI', 'Adalah Hak pekerja / buruh yang diterima dan dinyatakan dalam bentuk uang sebagai imbalan dari pengusaha / pemberi kerja kepada pekerja / buruh yang ditetapkan dan dibayarkan menurut suatu perjanjian kerja ,kesepakatan,atau peraturan perundang-undangan termasuk tunjangan bagi pekerja / buruh dan keluarganya atas suatu pekerjaan dan atau jasa yang telah dilakukan.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('205', '1', null, '10', 'UU 13 / 2003', 'Adalah Singkatan yang umum digunakan dalam penyebutan Undang-Undang No 13 Tahun 2003 Tentang Ketenagakerjaan. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('206', '1', null, '10', 'UU 21 / 2007', 'UU 21 / 2007 atau sering juga disebut UU PTPPO Adalah Singkatan yang umum digunakan dalam penyebutan Undang-Undang No 21 Tahun 2007 Tentang Pemberantasan Tindak Pidana Perdagangan Orang.', '4', '8', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('207', '1', null, '10', 'UU 39 /2004', 'Adalah singkatan yang umum digunakan dalam penyebutan Undang-Undang Republik Indonesia No 39 Tahun 2004 Tentang Penempatan Dan Perlindungan Tenaga Kerja Indonesia di Luar Negeri.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('208', '1', null, '173', 'Verifikasi Dokumen TKI', 'Adalah Pemeriksaan keabsahan dokumen TKI yang meliputi  a. Kartu Tanda Penduduk (KTP), ijazah pendidikan terakhir, akte kelahiran atau surat keterangan kenal lahir; b. surat keterangan status perkawinan bagi yang telah menikah melampirkan foto copy buku nikah; c. surat keterangan izin suami atau istri, izin orang tua, atau izin wali; d. sertifikat kompetensi kerja; e. surat keterangan sehat berdasarkan hasil hasil pemeriksaan kesehatan dan psikologi; f. paspor yang diterbitkan oleh kantor imigrasi setempat; g. visa kerja; h. perjanjian penempatan TKI; i. perjanjian kerja; dan j. Kartu Tenaga Kerja Luar Negeri (KTKLN).', '4', '8', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('209', '1', null, '173', 'Visa', 'Izin masuk ke negara lain atau izin tinggal sementara di negara lain yang diberikan oleh pejabat pemerintah yang berwenang di negara yang dikunjungi.', '4', '14', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('210', '1', null, '173', 'Visa Kerja', 'Izin tertulis yang diberikan oleh pejabat yang berwenang pada perwakilan suatu negara yang memuat persetujuan untuk masuk dan melakukan pekerjaan di negara yang bersangkutan.', '4', '21', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('211', '1', null, '173', 'Visa on Arrival', 'Dokumen izin masuk seseorang ke suatu negara yang bisa diperoleh langsung di perbatasan antar negara atau di kedutaan dimana negara tersebut mempunyai Konsulat Jenderal  atau kedutaan asing.  ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('212', '1', null, '173', 'Visa Wakalah', 'Adalah salah satu bentuk dokumen surat permintaan TKI yang berasal dari pengguna di Saudi Arabia. Kata Wakalah berarti mewakilkan kepada PPTKIS/Mitra Usahanya untuk memproses penempatan TKI berdasarkan persetujuan penerbitan Visa yang dimilki oleh pengguna.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-18 12:32:08', '1', null, null, '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1', '2014-06-18 12:32:08', '1');
INSERT INTO `h_info_tki` VALUES ('221', '75', null, '240', 'Bagaimana proses pemulangan TKI yang sudah habis kontrak ke kota domisili TKI agar TKI terhindar dari oknum-oknum yang tidak bertanggungjawab?', 'Harus dikondisikan dan didata lengkap dan menggunakan armada transport angkutan yang legal dan terdata.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-22 16:59:44', '19', null, null, '2014-06-22 17:21:27', '8', '2014-06-22 17:22:35', '13', '2014-06-22 16:59:44', '19');
INSERT INTO `h_info_tki` VALUES ('222', '192', null, '240', 'Untuk TKI yang terkena hukum pidana atau bahkan sudah di hukum penjara, bagaimana tindak lanjut/peran BNP2TKI dalam menangani masalah tersebut dan bagaimana TKI tersebut dapat dipulangkan ke Indonesia?', 'Peran BN2TKI, memonitor perkembangan permasalahan dengan cara berkoordinasi dengan perwakilan RI dan Kemenlu, BNP2TKI siap memfasilitasi kelengkpan data pendukung (dalam negeri) dalam rangka penyelesaian kasus.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:00:28', '19', null, null, '2014-06-22 17:21:30', '8', '2014-06-22 17:22:51', '13', '2014-06-22 17:00:28', '19');
INSERT INTO `h_info_tki` VALUES ('223', '194', null, '240', 'Bagaimana peran pemerintah khususnya BNP2TKI dalam menangani persoalan tentang TKI illegal dan apa saja upaya yang dilakukan untuk mengurangi TKI illegal tersebut?', 'Berikan sosialisasi dan edukasi perlindungan secara berkelanjutan hingga ke pelosok daerah.', '4', '122', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:01:35', '19', null, null, '2014-06-22 17:21:33', '8', '2014-06-22 17:22:54', '13', '2014-06-22 17:01:35', '19');
INSERT INTO `h_info_tki` VALUES ('224', '46', null, '126', 'Untuk TKI yang membawa anak dari negara tempat kerja TKI, apakah anak tersebut dibawa pulang ke Indonesia dan bagaimana proses hukum bagi anak tersebut?', 'Indonesia menerapkan asas <em>ius sanguinis</em> yang mengakui kewarganegaraan berdasarkan garis keturunan dan juga pengaturan perihal tersebut tertuang dalam Undang-Undang Nomor 12 Tahun 2006 tentang Kewarganegaraan Indonesia , selanjutnya WNI yang melahirkan di luar negeri akan mendapatkan akta kelahiran yang akan diproses oleh KBRI setempat, jika anak tersebut merupakan anak dari WNI. Akan tetapi jika orang tua dari sang anak merupakan warga negara asing maka akan diberi pilihan untuk menentukan kewarganegaraan untuk dirinya dikarenakan Indonesia tidak menganut sistem kewarganegaraan ganda (<em>bipatride</em>).<br />\r\n', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:17:43', '20', null, null, '2014-06-22 17:24:09', '9', '2014-06-22 17:24:39', '14', '2014-06-22 17:17:43', '20');
INSERT INTO `h_info_tki` VALUES ('225', '195', null, '126', 'Masalah yang sering terjadi terhadap TKI informal khususnya PLRT menjadi sorot bagi pemerintah dan warga Indonesia mengingat hal-hal tersebut tidak lazim. Hal itu bisa saja di picu oleh faktor kurangnya pengetahuan TKI tersebut terkait soal bahasa maupun nilai mata uang. Bagaimana tindakan pemerintah dan stakeholder terkait dalam meningkatkan mutu TKI tersebut?', 'Pemerintah telah menyediakan website Jobs Info <a href=\"http://jobsinfo.bnp2tki.go.id/portal/\">http://jobsinfo.bnp2tki.go.id/portal/</a> yang mana di bagian profile pada web tersebut menyediakan link yang digunakan setiap orang di dunia dalam mengetahui soal bahasa yang bisa digunakan maupun budaya pada negara tersebut. Terkait dengan nilai mata uang telah disediakan data tentang standar biaya di negara tujuan penempatan (seluruh dunia) <a href=\"http://www.numbeo.com/cost-of-living/\">http://www.numbeo.com/cost-of-living/</a> atau <a href=\"http://www.xpatulator.com/cost-of-living-article/International-Cost-of-Living-October-2013_490.cfm\">http://www.xpatulator.com/cost-of-living-article/International-Cost-of-Living-October-2013_490.cfm</a>&nbsp; sebagai acuan dan informasi bagi TKI.<br />\r\n', '4', '151', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:18:28', '20', null, null, '2014-06-22 17:24:13', '9', '2014-06-22 17:24:44', '14', '2014-06-22 17:18:28', '20');
INSERT INTO `h_info_tki` VALUES ('226', '56', null, '126', 'Berapakah standar gaji untuk tahun 2013 bagi para TKI yang bekerja diluar negri, mohon rinciannya untuk semua Negara.', 'Standar gaji untuk tahun 2013 untuk negara yang sudah mempunyai kerjasama seperti, Korea dan Jepang dapat menghubungi Direktorat PPP (Pelayanan Penempatan Pemerintah), selain itu sebagai sebuah rujukan standar gaji dari tiap negara dapat melihat list di alamat link <a href=\"http://www.salaryexplorer.com/salary-survey.php\">http://www.salaryexplorer.com/salary-survey.php</a> yang merupakan salah satu rujukan dalam mengetahui standar minimum gaji pekerja di negara penempatan.<br />\r\n', '4', '28', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:19:05', '20', null, null, '2014-06-22 17:24:16', '9', '2014-06-22 17:24:47', '14', '2014-06-22 17:19:05', '20');
INSERT INTO `h_info_tki` VALUES ('227', '26', null, '126', 'Apa betul hukum pancung tidak berlaku untuk keluarga kerajaan Saudi Arabia ?', 'TIDAK BETUL, karena negara Arab Saudi mengakui adanya Hukum Islam yang didalamnya tidak memandang jabatan ataupun pangkat ataupun garis keturunan dalam memberikan sebuah hukuman kepada tersangka yang melakukan kesalahan, hal tersebut diberlakukan secara setara dan adil sesuai dengan syariah Islam dan merujuk pada Al-Qur&rsquo;an.<br />\r\n', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-22 17:19:48', '20', null, null, '2014-06-22 17:24:20', '9', '2014-06-22 17:24:50', '14', '2014-06-22 17:19:48', '20');
INSERT INTO `h_info_tki` VALUES ('228', '154', null, '126', 'Jika TKI Korea telah mengikuti preliminary training tapi tidak diberangkatkan ke Korea, apakah biaya preliminary akan dikembalikan ?', 'Seluruh biaya yang disetorkan sepanjang belum digunakan oleh CTKI Korea dapat diambil kembali.', '4', '10', null, null, null, null, null, null, null, null, null, null, '2014-06-23 15:49:10', '6', null, null, '2014-06-23 15:49:10', '6', '2014-06-23 15:49:10', '6', '2014-06-23 15:49:10', '6');
