INSERT INTO `h_info_tki` VALUES ('230', '229', null, '126', 'Bolehkan melamar pekerjaan ke Korea tanpa melalui BNP2TKI ?', 'Jika programnya EPS-TOPIK (Jenis Visa Kerja E 9) tidak boleh.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-23 15:54:10', '6', null, null, '2014-06-23 15:54:10', '6', '2014-06-23 15:54:10', '6', '2014-06-23 15:54:10', '6');
INSERT INTO `h_info_tki` VALUES ('231', '115', null, '126', 'Jika nama di Paspor berbeda dengan daftar nama yg dikeluarkan BNP2TKI, Balaimana CTKI harus melapor ?', 'Jika kontrak kerja (SLC) belum dikeluarkan oleh HRD Korea, maka datang langsung ke Kantor BNP2TKI untuk lapor data, namun jika Convirmation Certificate of Visa Issuance (CCVI) telah diterbitkan oleh HRD Korea, maka Paspor harus di Revisi.', '4', '154', null, null, null, null, null, null, null, null, null, null, '2014-06-23 15:56:28', '6', null, null, '2014-06-23 15:56:28', '6', '2014-06-23 15:56:28', '6', '2014-06-23 15:56:28', '6');
INSERT INTO `h_info_tki` VALUES ('232', '229', null, '126', 'Apakah setiap CTKI Korea wajib mengikuti  LPK ?', 'Tidak wajib', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:00:40', '6', null, null, '2014-06-23 16:00:40', '6', '2014-06-23 16:00:40', '6', '2014-06-23 16:00:40', '6');
INSERT INTO `h_info_tki` VALUES ('233', '229', null, '126', 'Seberapa lama CTKI Korea menunggu untuk mendapat panggilan terbang ke Korea ?', 'Jika seluruh data CTKI benar (tidak ada kesalahan) sejak dinyatakan Lulus Ujian&nbsp; dan mendapat Sertifikat EPS-TOPIK paling cepat 6 (enam) bulan.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:01:15', '6', null, null, '2014-06-23 16:01:15', '6', '2014-06-23 16:01:15', '6', '2014-06-23 16:01:15', '6');
INSERT INTO `h_info_tki` VALUES ('234', '229', null, '126', 'Apakah Tenaga Profesional dibidang tertentu bisa bekerja di Korea ?', 'Bisa sepanjang non program EPS-TOPIK.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:01:57', '6', null, null, '2014-06-23 16:01:57', '6', '2014-06-23 16:01:57', '6', '2014-06-23 16:01:57', '6');
