INSERT INTO `h_info_tki` VALUES ('243', '242', null, '126', 'Apakah boleh CTKI mengikuti Ujian EPS-TOPIK jika tidak mempunyai sertifikat dari LPK ?', 'Boleh (Ujian EPS-TOPIK dapat ikuti oleh siappun tanpa memiliki sertifikat dari LPK).', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:13:01', '6', null, null, '2014-06-23 16:13:01', '6', '2014-06-23 16:13:01', '6', '2014-06-23 16:13:01', '6');
