INSERT INTO `h_info_tki` VALUES ('245', '244', null, '126', 'Untuk Program G to G Korea, apakah alamat website EPS.go.kr itu synchronize dengan bnp2tki.go.id. Jadi jika nama TKI disek di website EPS.go.kr itu telah keluar SLCnya, sedangkan di website bnp2tki.go.id belum ada, bisa dijadikan acuan atau tidak. bahwa  nama TKI tersebut sudah pasti keluar SLCnya nanti di website bnp2tki.go.id ?', 'Website EPS.go.kr beda dengan website bnp2tki.go.id, jika CTKI telah mendapatkan SLC dari Perusahaan melalui HRD Korea maka BNP2TKI pasti menampilkannya pada website bnp2tki.go.id yang sekaligus untuk mengikuti kegiatan preliminary training.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:14:42', '6', null, null, '2014-06-23 16:14:42', '6', '2014-06-23 16:14:42', '6', '2014-06-23 16:14:42', '6');
INSERT INTO `h_info_tki` VALUES ('246', '36', null, '173', 'Apakah ada system calling visa untuk negara Korea ?', 'Penempatan TKI Korea Program G to G tidak ada system calling visa.', '4', '4', null, null, null, null, null, null, null, null, null, null, '2014-06-23 16:17:02', '6', null, null, '2014-06-23 16:17:02', '6', '2014-06-23 16:17:02', '6', '2014-06-23 16:17:02', '6');
