INSERT INTO `h_info_tki` VALUES ('248', '247', null, '126', 'Apakah negara Korea menerima TKI mandiri atau tidak melalui program G to G ?', 'Negara Korea dapat menerima TKI mandiri tetapi CTKI harus mempunyai Komptensi sesuai bidang yang dibutuhkan.Negara Korea dapat menerima TKI mandiri tetapi CTKI harus mempunyai Komptensi sesuai bidang yang dibutuhkan.<br />\n', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:26:02', '6', null, null, '2014-06-23 17:26:02', '6', '2014-06-23 17:26:02', '6', '2014-06-23 17:26:02', '6');
INSERT INTO `h_info_tki` VALUES ('249', '250', null, '126', 'Apakah Sertifikat EPS-TOPIK mempunyai masa berlaku?', 'Punya masa berlaku', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:28:31', '6', '2014-06-23 17:34:32', '6', '2014-06-23 17:28:31', '6', '2014-06-23 17:28:31', '6', '2014-06-23 17:34:32', '6');
