INSERT INTO `h_info_tki` VALUES ('251', '250', null, '126', 'Jika masa masa berlaku sertifikat sudah habis apakah bisa diperpanjang atau harus mengikuti kembali EPS-TOPIK ?', 'Masa berlaku sertifikat tidak dapat diperpanjang dan harus mengikuti ujian ulang dan lulus.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:35:26', '6', null, null, '2014-06-23 17:35:26', '6', '2014-06-23 17:35:26', '6', '2014-06-23 17:35:26', '6');
INSERT INTO `h_info_tki` VALUES ('252', '250', null, '126', 'Berapa lama masa berlaku sertifikat EPS-TOPIK ?', '2 (dua) Tahun sejak dinyatakan lulus ujian EPS-TOPIK', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:35:29', '6', null, null, '2014-06-23 17:35:29', '6', '2014-06-23 17:35:29', '6', '2014-06-23 17:35:29', '6');
INSERT INTO `h_info_tki` VALUES ('253', '250', null, '126', 'Bagaimana cara memperpanjang masa berlaku  sertifikat EPS-TOPIK?', 'Sertifikat tidak dapat diperpanjang dan jika ingin mendapatkan sertifikat, CTKI harus mengikuti ujian EPS-TOPIK dan lulus', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:38:38', '6', null, null, '2014-06-23 17:38:38', '6', '2014-06-23 17:38:38', '6', '2014-06-23 17:38:38', '6');
INSERT INTO `h_info_tki` VALUES ('254', '250', null, '126', 'Apakah TKI yang ingin mendaftar menjadi TKI Korea harus melalui Lembaga Bahasa Korea terlebih dahulu ?', 'CTKI dapat mendaftar menjadi CTKI Korea tanpa melalui Lembaga Bahasa Korea.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:39:10', '6', null, null, '2014-06-23 17:39:10', '6', '2014-06-23 17:39:10', '6', '2014-06-23 17:39:10', '6');
INSERT INTO `h_info_tki` VALUES ('255', '1', null, '126', 'Prosedur Bekerja di Jepang', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:43:20', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('256', '255', null, '126', 'Bagaimana prosedur menjadi TKI ke Jepang ?', 'Penempatan TKI ke Jepang hanya untuk jabatan Perawat di Rumah Sakit dan Perawat pada Panti Lanjut Usia dengan pendidikan miniamal lulusan D3 Keperawatan dan datang langsung ke Kantor BNP2TKI/BP3TKI/UP3TKI/LP3TKI di Daearh dimana CTKI tinggal setelah ada pengumuman penerimaan untuk TKI dimaksud pada website bnp2tki.go.id.', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:47:22', '6', null, null, '2014-06-23 17:47:22', '6', '2014-06-23 17:47:22', '6', '2014-06-23 17:47:22', '6');
INSERT INTO `h_info_tki` VALUES ('257', '1', null, '126', 'Program G to P', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:49:09', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('258', '156', null, '126', 'Bagaimana prosedur menjadi TKI Program G to P ke Malaysia ?', 'Penempatan TKI ke Malaysia Program G to P mayoritas untuk kaum Wanita, dan jika ada penerimaan untuk TKI dimaksud maka akan diumumkan melalui website bnp2tki.go.id dan prosesnya CTKI dapat datang langsung langsung ke Kantor BNP2TKI/BP3TKI/ UP3TKI/LP3TKI di Daearh dimana CTKI tinggal.', '4', '5', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:51:01', '6', null, null, '2014-06-23 17:51:01', '6', '2014-06-23 17:51:01', '6', '2014-06-23 17:51:01', '6');
INSERT INTO `h_info_tki` VALUES ('259', '1', null, '126', 'Pinjaman KUR', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 17:52:38', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('260', '259', null, '126', 'KUR/Pinjaman uang di BRI untuk ke Korea diberikan sebelum TKI berangkat ke Jakarta atau setelah di Jakarta ?', 'KUR dapat diberikan sebelum TKI berangkat ke Jakarta maupun setelah pulang dari Jakarta, hal ini tergantung kesepakatan antara CTKI dengan pihak BRI setempat.', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-23 18:03:20', '6', null, null, '2014-06-23 18:03:20', '6', '2014-06-23 18:03:20', '6', '2014-06-23 18:03:20', '6');
INSERT INTO `h_info_tki` VALUES ('261', '1', null, '126', 'Pindah Jabatan Korea', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 18:04:08', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('262', '261', null, '126', 'Jika TKI Korea yang baru bekerja sekitar 2 (dua) tahun dan merasa tidak mampu lagi bekerja karena merasa kesulitan untuk mengikuti cara kerja dan tingkat kesulitan menggunakan mesin-mesin yang ada, bisakah jika TKI Korea tersebut ingin pindah Majikan, Jika bisa apa syarat-syaratnya ?', 'TKI dapat pindah kerja dengan syarat sebagai berikut :<br />\n<ol style=\"list-style-type:lower-alpha;\">\n	<li>\n		Perusahaan Bangkut/Pailit</li>\n	<li>\n		Pekerjaan yang dilakukan oleh TKI berdampak menimbulkan penyakit, dan penyakit tersebut akan&nbsp; membahayakan bagi kesehatan TKI tersebut.</li>\n	<li>\n		Minta Ijin pada Perusahaan dan melaporkannya kepada pihak Kementerian Tenaga Kerja Setempat agar datanya dapat iproses untuk ditempatkan bekerja pada Perusahaan yang baru, namun jika dalam jangka waktu 3 (tiga) bulan TKI tersebut tidak ada Perusahaan yang mempekerjakannya, maka TKI tersebut akan dipulangkan ke negara asalnya.</li>\n</ol>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 18:05:09', '6', null, null, '2014-06-23 18:05:09', '6', '2014-06-23 18:05:09', '6', '2014-06-23 18:05:09', '6');
INSERT INTO `h_info_tki` VALUES ('263', '247', null, '126', 'Bagaimana proses menjadi CTKI Korea Mandiri ?', 'Penempatan TKI ke Korea berdasarkan atas MoU antara Pemerintah Indonesia dan Pemerintah Korea untuk penempatan pada pengguna berbadan hukum (perusahaan) disektor pekerjaan manufacture, Construction, Fishing, Agro Industri dan Services yang ditempatkan di perusahaan menengah dan kecil. Oleh karena itu penempatannya dilakukan oleh BNP2TKI dengan kategori E9, untuk itu penempatan TKI ke Korea tidak dilakukan secara mandiri oleh calon TKI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 18:07:11', '6', null, null, '2014-06-23 18:07:11', '6', '2014-06-23 18:07:11', '6', '2014-06-23 18:07:11', '6');
