INSERT INTO `h_info_tki` VALUES ('265', '264', null, '126', 'Tentang medical ke Korea untuk calon TKI  yang perokok, apa ada kendalanya  ?', 'Bagi&nbsp; calon TKI yang merokok pada umunya akan ada gangguan di paru paru atau pernapasan sehingga mempengaruhi hasil medical. Bagi yang hasil medical terkena paru paru maka akan dinyatakan unfit. Disamping itu TKI setelah tiba di Korea akan dilakukan meical ulang. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-23 18:08:40', '6', '2014-06-24 09:47:16', '6', '2014-06-23 18:08:40', '6', '2014-06-23 18:08:40', '6', '2014-06-24 09:47:16', '6');
