INSERT INTO `h_info_tki` VALUES ('271', '269', null, '126', 'Informasi website mana yang harus kita ikuti, eps.co.kr atau bnp2tki.go.id ?', 'Informasi yang digunakan sebagai dasar proses penempatan TKI ke Korea program G to G adalah www.bnp2tki.go.id.', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:11:39', '6', null, null, '2014-06-24 10:11:39', '6', '2014-06-24 10:11:39', '6', '2014-06-24 10:11:39', '6');
INSERT INTO `h_info_tki` VALUES ('272', '270', null, '126', 'Berapa standar gaji untuk program G to G Korea (masing-masing sector) ?', 'Gaji minimal adalah 1.060.000 won per bulan, sama untuk semua sektor. ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:11:42', '6', null, null, '2014-06-24 10:11:42', '6', '2014-06-24 10:11:42', '6', '2014-06-24 10:11:42', '6');
