INSERT INTO `h_info_tki` VALUES ('278', '270', null, '126', 'Berapa standar gaji untuk program G to G Jepang (careorker dan nurse) ?', 'Nurse dan careworker di Jepang bekerja di Jepang dengan jabatan asisten nurse dan asisten careworker dengan gaji asisten nurse dan asisten careworker di Jepang antara 140.000 &ndash; 200.000 yen per bulan. Jika sudah lulus ujian nasional Jepang maka jabatannya menjadi kangoshi dan kaigofukushishi dengan gaji diatas 200.000 yen.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:45:32', '6', null, null, '2014-06-24 10:45:32', '6', '2014-06-24 10:45:32', '6', '2014-06-24 10:45:32', '6');
INSERT INTO `h_info_tki` VALUES ('279', '155', null, '126', 'Apa saja syarat cara menjadi TKI Korea dengan program G to G ? ', '<ol>\n	<li>\n		Lulus tes EPS TOPIK, dengan melampirkan foto copy sertifikat EPS TOPIK.</li>\n	<li>\n		Tidak sedang dicekal dicekal pergi ke luar negeri.</li>\n	<li>\n		Tidak pernah di deportasi dari negara Korea/ilegal stay.</li>\n	<li>\n		Tidak mempunyai catatan kriminal dan tidak pernah dihukum atau dipenjara.</li>\n	<li>\n		&nbsp;Mengisi formulir pendaftaran dengan jelas dan benar.</li>\n	<li>\n		&nbsp;Foto copy KTP usia 18 -39 tahun.</li>\n	<li>\n		Foto copy Kartu Keluarga</li>\n	<li>\n		Foto copy ijasah pendidikan formal serendah-rendahnya lulusan SLTP atau sederajat.</li>\n	<li>\n		Foto copy paspor yang masih berlaku</li>\n	<li>\n		&nbsp;Pas foto terbaru berwarna ukuran 3 X 4 cm sebanyak 2 lembar.</li>\n	<li>\n		Asli surat keterangan catatan kepolisian (SKCK)</li>\n	<li>\n		Asli sertifikat hasil pemeriksaan kesehatan (medical check up).</li>\n	<li>\n		Asli bukti pendaftaran pencari kerja (AK I) dari dinas yang menangani bidang ketenagakerjaan.</li>\n	<li>\n		Asli surat ijin dari Suami/isteri/Orang tua atau wali diatas meterai Rp. 6000,- diketahui lurah atau kepala desa.</li>\n</ol>\n', '4', '236', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:45:35', '6', null, null, '2014-06-24 10:45:35', '6', '2014-06-24 10:45:35', '6', '2014-06-24 10:45:35', '6');
INSERT INTO `h_info_tki` VALUES ('280', '1', null, '126', 'Cost Structure TKI Korea ', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:49:42', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('281', '280', null, '126', 'Berapa cost structure untuk program G to G ke Korea ?', 'Cost Structure (biaya penempatan ke Korea) sesuai dengan Keputusan Menteri Tenaga Kerja dan Transmigrasi Nomor KEP.17/MEN/II/2011 tanggal 7 Pebruari 2013 sebesar &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp 8.970.000,- dengan rincian sebagai berikut :<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a.&nbsp;&nbsp;&nbsp; Pemeriksaan Kesehatan (Medical Chek Up)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Rp. &nbsp;&nbsp;&nbsp;475.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; b.&nbsp;&nbsp;&nbsp; Pemeriksanaan Psikologi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp.&nbsp;&nbsp;&nbsp; 250.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c.&nbsp;&nbsp;&nbsp; Visa Kerja&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp.&nbsp; &nbsp; 500.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; d.&nbsp;&nbsp;&nbsp; Asuransi Perlindungan (5 Tahun)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp.&nbsp;&nbsp;&nbsp; 760.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.&nbsp;&nbsp;&nbsp; Preliminary Training (6 hari)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp. 1.175.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; f.&nbsp;&nbsp;&nbsp;&nbsp; Tiket (Jakarta-Korea)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; USD&nbsp; 535.000,-<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; g.&nbsp;&nbsp;&nbsp; Air Port Tax&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rp.&nbsp;&nbsp;&nbsp; 150.000,-', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-24 10:51:36', '6', null, null, '2014-06-24 10:51:36', '6', '2014-06-24 10:51:36', '6', '2014-06-24 10:51:36', '6');
