INSERT INTO `h_info_tki` VALUES ('333', '1', null, '240', 'TKI sakit', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:35:51', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('334', '332', null, '240', 'Untuk TKI yang membawa anak dari Negara tempat kerja TKI, apakah anak tersebut dibawa pulang ke Indonesia dan bagaimana proses hukum bagi anak tersebut?', 'Anak TKI dari negara penmpatan di bawa pulang ke Indonesia mengikuti ibu nya, dari pihak KJRI/KBRI membantu/mengurus surat keterangan lahir di negara penempatan untuk membuat SPLP.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:37:19', '6', null, null, '2014-06-24 14:37:19', '6', '2014-06-24 14:37:19', '6', '2014-06-24 14:37:19', '6');
INSERT INTO `h_info_tki` VALUES ('335', '333', null, '240', 'Jika TKI terkena penyakit menular, apakah TKI tersebut dapat dipulangkan ke Indonesia atau bahkan di rujuk kerumah sakit setempat? ', 'Sesuaikan dengan ketentuan Negara penempatan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:37:21', '6', null, null, '2014-06-24 14:37:21', '6', '2014-06-24 14:37:21', '6', '2014-06-24 14:37:21', '6');
INSERT INTO `h_info_tki` VALUES ('336', '1', null, '240', 'Pembelaan TKI bermasalah hukum', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:37:58', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('337', '336', null, '240', 'Untuk penempatan dan perlindungan TKI di luar negeri, apabila TKI mendapat perlakuan tindak kekerasan dari majikan dan TKI tersebut membela diri dengan melukai majikan, bagaimana peran pemerintah Indonesia dalam membela hak TKI tersebut?', 'Permasalahan TKI yang berhubungan dengan tindak pidana proses penyelesaiannya dilakukan melalui kepolisian dan pengadilan. Pihak Pemerintah berkoordinasi dengan pihak KBRI untuk menyediakan kuasa hukum untuk TKI yang bermasalah.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:39:46', '6', null, null, '2014-06-24 14:39:46', '6', '2014-06-24 14:39:46', '6', '2014-06-24 14:39:46', '6');
INSERT INTO `h_info_tki` VALUES ('338', '139', null, '240', 'Apabila TKI ingin memutuskan  pulang ke Indonesia sebelum masa kontrak atau perjanjian kontraknya habis, bagaimana proses pemulangannya?', 'TKI harus merujuk pada Perjanjian Kerja (PK) yang mengatur hak dan kewajiban.', '4', '7', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:39:50', '6', null, null, '2014-06-24 14:39:50', '6', '2014-06-24 14:39:50', '6', '2014-06-24 14:39:50', '6');
INSERT INTO `h_info_tki` VALUES ('339', '1', null, '240', 'KDEI Taipei', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:41:07', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('340', '336', null, '240', 'Bagaimana jika seorang TKI yang bekerja di Taiwan dan mengalami permasalahan hukum, kemudian TKI tersebut tidak mendapatkan bantuan hukum dari pihak KDEI di Taiwan?', 'KDEI berkewajiban memberikan bantuan hukum kepada TKI yang bekerja di Taiwan, diharapkan melapor kepada KDEI. ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:42:48', '6', null, null, '2014-06-24 14:42:48', '6', '2014-06-24 14:42:48', '6', '2014-06-24 14:42:48', '6');
INSERT INTO `h_info_tki` VALUES ('341', '339', null, '240', 'Apakah yang bisa dibantu oleh pihak kamar dagang yang ada di Taiwan untuk para TKI yang bekerja disana?', '<ul>\n	<li>\n		KDEI membantu jika ada/ timbul Permasalahan;</li>\n	<li>\n		KDEI melakukan pembinaan dengan para TKI, dan;</li>\n	<li>\n		KDEI membantu menerbitkan dokumen/ perpanjangan paspor.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:42:50', '6', null, null, '2014-06-24 14:42:50', '6', '2014-06-24 14:42:50', '6', '2014-06-24 14:42:50', '6');
INSERT INTO `h_info_tki` VALUES ('342', '1', null, '240', 'Pemalsuan dokumen TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:43:55', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('343', '75', null, '240', 'Apakah alasan-alasan TKI sulit untuk dipulangkan ke Indonesia ? Apakah masing-masing Negara mempunyai tingkat kesulitan berbeda untuk kepulangan TKI', '<ol style=\"list-style-type:lower-alpha;\">\n	<li>\n		Kepulangan TKI dalam kondisi normal (kontrak)</li>\n</ol>\n<ul>\n	<li>\n		Pengguna membantu membuatkan exit permit.</li>\n</ul>\n<ol style=\"list-style-type:lower-alpha;\">\n	<li value=\"2\">\n		Kepulangan TKI dalam kondisi bermasalah</li>\n</ol>\n<ul>\n	<li>\n		Permasalahan &ndash; permasalahan tersebut diselesaikan terlebih dahulu.</li>\n</ul>\n', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:46:00', '6', null, null, '2014-06-24 14:46:00', '6', '2014-06-24 14:46:00', '6', '2014-06-24 14:46:00', '6');
INSERT INTO `h_info_tki` VALUES ('344', '342', null, '240', 'Bagaimana cara menangani TKI yang bermasalah seperti pemalsuan dalam pengurusan dokumen (Undocumented), penyimpangan prosedur pendidikan dan pelatihan, penyimpangan penampungan sehingga menimbulkan permasalahan dikemudian hari mulai dari pra penempatan, masa penempatan, purna penempatan ?', 'Verifikasi dokumen intensif di BP3TKI/P4TKI/LP3TKI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:46:03', '6', '2014-06-24 14:47:41', '6', '2014-06-24 14:46:03', '6', '2014-06-24 14:46:03', '6', '2014-06-24 14:47:41', '6');
INSERT INTO `h_info_tki` VALUES ('345', '1', null, '240', 'Hak-Hak TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:51:51', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('346', '1', null, '240', 'Kewajiban TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:52:09', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('347', '345', null, '240', 'Apa sajakah hak-hak TKI yang diluar pengaturan UU No.39 Tahun 2004 pasal 8? Apabila hak-hak TKI tidak terpenuhi bagaimana mengatasinya ?', '<ul>\n	<li>\n		Menyimpan dan memegang dokumen TKI (copy paspor, PK);</li>\n	<li>\n		Memperoleh hari libur;</li>\n	<li>\n		Memperoleh cuti;</li>\n	<li>\n		Memperoleh uang lembur apabila bekerja di hari libur.</li>\n</ul>\nApabila yang menjadi hak TKI tidak terpenuhi, dapat menghubungi Perwakilan RI yang ada di negara penempatan/ agency/BNP2TKI melalui nomor pengaduan untuk kemudian memanggil perusahaan yang memberangkatkan untuk meminta pertanggungjawaban.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:53:34', '6', null, null, '2014-06-24 14:53:34', '6', '2014-06-24 14:53:34', '6', '2014-06-24 14:53:34', '6');
INSERT INTO `h_info_tki` VALUES ('348', '346', null, '240', 'Apa sajakah Kewajiban-kewajiban TKI yang diluar pengaturan uu no 39 tahun 2004 pasal 9? Dan bagaimana menangani apabila TKI tidak memenuhi kewajiban-kewajibannya ?', '<ul>\n	<li>\n		Memahami seluruh isi perjanjian kerja;</li>\n	<li>\n		Mengetahui nama, alamat, No. Telp. agency/perwakilan RI/ BNP2TKI/Pengguna;</li>\n	<li>\n		Menyimpan dan memegang dokumen TKI;</li>\n	<li>\n		Mengetahui pekerjaan yang harus dikerjakan;</li>\n	<li>\n		Menyesuaikan diri dengan adat istiadat;</li>\n	<li>\n		Menyesuaikan diri dan menjalin hubungan yang baik denganPengguna;</li>\n	<li>\n		Bersikap ramah.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:53:37', '6', null, null, '2014-06-24 14:53:37', '6', '2014-06-24 14:53:37', '6', '2014-06-24 14:53:37', '6');
INSERT INTO `h_info_tki` VALUES ('349', '1', null, '240', 'Pengaduan palsu TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:54:02', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('351', '349', null, '240', 'Bagaimana apabila TKI memberikan keterangan pengaduan palsu seperti memberikan keterangan berlebihan atau bohong ?', '<ul>\n	<li>\n		Sebelum dimintai keterangan terlebih dahulu dijelaskan maksud dan tujuan serta sanksi/ akibat hukum yang ditimbulkan apabila memberikan keterangan palsu.</li>\n	<li>\n		TKI menanggung konsekuensi abila ada tuntutan dari pihak-pihak yang merasa dirugikan.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:58:17', '6', null, null, '2014-06-24 14:58:17', '6', '2014-06-24 14:58:17', '6', '2014-06-24 14:58:17', '6');
INSERT INTO `h_info_tki` VALUES ('352', '26', null, '240', 'Bagaimana cara memberikan bantuan hukum terhadap TKI yang ditahan/dipenjara diluar negeri sedangkan  pengaturan undang-undang Negara kita berbeda dengan peraturan luar negeri ?', '<ul>\n	<li>\n		Apabila ada TKI/WNI yang ditahan/dipenjara maka Perwakilan RI akan melakukan bantuan hukum berupa pendampingan dengan menyewa pengacara setempat, sedang peran BNP2TKI adalah memonitor perkembangan permasalahan TKI dan siap untuk memfasilitasi apabila diperlukan&nbsp; data-data dukung dari dalam negeri maupun memberangkatkan keluarga atau pihak terkait dalam rangka pendampingan maupun saksi yang meringankan kasus TKI.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 14:58:19', '6', null, null, '2014-06-24 14:58:19', '6', '2014-06-24 14:58:19', '6', '2014-06-24 14:58:19', '6');
INSERT INTO `h_info_tki` VALUES ('353', '1', null, '240', 'Mediasi TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:03:33', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('354', '353', null, '240', 'Bagaimanakah cara menyelesaikan mediasi dengan TKI ? bagaimana apabila dalam penyelesaian mediasi tidak menemui win-win solusion ? ', '<ul>\n	<li>\n		Mediasi pada dasarnya merupakan negosiasi yang mengikutsertakan pihak ketiga yang ahli &nbsp;(Mediator) yang dapat membantu para pihak dalam sengketa (CTKI/TKI/Keluarga dengan pihak yang dituntut)&nbsp; dengan mengkoordinasikan kegiatan-kegiatan, dan agar lebih efektif dalam bernegosiasi sehingga terselesaikan masalaha TKI.</li>\n	<li>\n		Apabila tidak menemui win-win solution mediator berhak menyarankan untuk menempuh jalur hukum/litigasi.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:05:12', '6', null, null, '2014-06-24 15:05:12', '6', '2014-06-24 15:05:12', '6', '2014-06-24 15:05:12', '6');
INSERT INTO `h_info_tki` VALUES ('355', '353', null, '240', 'Bagaimana apabila Permasalahan TKI sudah menemui win-win solusion dan sudah dibuat akta perdamain akan tetapi ditemukan pengaduan baru atau suatu saat pihak TKI merasa tidak puas dikemudian hari ?', 'Maka dapat dilakukan mediasi ulang dengan menyertakan bukti-bukti mediasi terdahulu. Mediator dapat mempertanyakan kesepakatan yang tidak dilakukan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:05:15', '6', null, null, '2014-06-24 15:05:15', '6', '2014-06-24 15:05:15', '6', '2014-06-24 15:05:15', '6');
INSERT INTO `h_info_tki` VALUES ('356', '1', null, '240', 'TKI depresi', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:06:20', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('357', '194', null, '240', 'Apakah permasalahan TKI yang illegal dapat perlindungan Hukum dari BNP2TKI ?', 'Dapat, TKI illegal hanya masalah prosedur penempatannya, jadi tetap dapat perlindungan sebagai warga negara, tentunya BNP2TKI memfasilitasikannya dan berkoordinasi dengan pihak-pihak terkait dui Indonesia dan di luar negeri sehinga hak TKI ybs terpenuhi dan terlindungi.&nbsp;&nbsp; ', '4', '1', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:08:37', '6', null, null, '2014-06-24 15:08:37', '6', '2014-06-24 15:08:37', '6', '2014-06-24 15:08:37', '6');
INSERT INTO `h_info_tki` VALUES ('358', '356', null, '240', 'Bagaimana penyelesaian bila menemukan TKI yang bermasalah di luar negeri akan tetapi TKI tersebut tidak dapat memberikan keterangan dengan jelas / sikap diam dikarenakan TKI tersebut depresi karna pulang dari luar negeri ?', 'Meminta klarifikasi dari perusahaan yang memberangkatkan TKI tersebut ke luar negeri dan meminta untuk melakukan klaim asuransi untuk pengobatan TKI tersebut. Apabila tidak ada PPTKIS dan asuransi maka BNP2TKI memfasilitasi pengobatan TKI tersebut di RS. POLRI Pusat.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:08:40', '6', null, null, '2014-06-24 15:08:40', '6', '2014-06-24 15:08:40', '6', '2014-06-24 15:08:40', '6');
INSERT INTO `h_info_tki` VALUES ('359', '1', null, '240', 'TKI meninggal', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:30:28', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('360', '1', null, '240', 'Perjanjian Kerja tidak sesuai', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:30:43', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('361', '359', null, '240', 'Bagaimana bila TKI yang dipulangkan dari luar negeri meninggal dunia dengan alasan TKI tersebut bunuh diri atau kecelakaan kerja dan pihak keluarga TKI melakuan visum et reverendum yang ditemukan bukti barub ahwa TKI tersebut terbukti organ tubuh Tk Itersebut tidak utuh, bagaimana mengatasinya ?', '<ul>\n	<li>\n		Melakukan klarifikasi kebenaranya dan&nbsp; Melakukan legalisasi terhadap hasil visum et reverendum, berkoordinasi dengan Kemenlu RI dan Perwakilan RI di Negara penempatan untuk melakukan pelaporan dan penuntutan;</li>\n</ul>\n<ul>\n	<li>\n		Isu pengambilan organ tubuh selama ini tidak pernah terbukti.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:32:11', '6', null, null, '2014-06-24 15:32:11', '6', '2014-06-24 15:32:11', '6', '2014-06-24 15:32:11', '6');
INSERT INTO `h_info_tki` VALUES ('362', '360', null, '240', 'Bagaimana mekanisme penyelesaian apabila penempatannya TKI tidak sesuai perjanjian, misalnya TKI tersebut diperjanjian dikerjakan di sebuah pabrik nyatanya setelah sampai di luar negeri dipekerjakan sebagai Pekerja sex komersial ?', '<ul>\n	<li>\n		Jika terbukti kepada pelaku penempatan untuk kemudian diproses/ ditunda secara hukum;</li>\n	<li>\n		TKI diselamatkan dan dipulangkan ke Indonesia.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:32:14', '6', null, null, '2014-06-24 15:32:14', '6', '2014-06-24 15:32:14', '6', '2014-06-24 15:32:14', '6');
INSERT INTO `h_info_tki` VALUES ('363', '1', null, '240', 'Masalah gaji dan asuransi TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:33:01', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('364', '1', null, '240', 'Ganti rugi', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:33:17', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('365', '363', null, '240', 'Bagaimanakah langkah-langkah mengurus gaji TKI, Asuransi TKI, dan Penggantian rugi TKI ?', '<ol style=\"list-style-type:lower-alpha;\">\n	<li>\n		Mengurus gaji:</li>\n</ol>\n<ul>\n	<li>\n		Bila TKI berada di Indonesia, mengurus melalui PPTKIS untuk dimintakan melalui agensi.</li>\n	<li>\n		Bila TKI berada di luar negeri, menghubungi Perwakilan RI setempat.</li>\n</ul>\n<ol style=\"list-style-type:lower-alpha;\">\n	<li value=\"2\">\n		Asuransi dan ganti rugi:</li>\n</ol>\n<ul>\n	<li>\n		Bila TKI berada di Indonesia, mengajukan klaim asuransi sesuai syarat-syarat yang ditentukan.</li>\n</ul>\n', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:37:43', '6', null, null, '2014-06-24 15:37:43', '6', '2014-06-24 15:37:43', '6', '2014-06-24 15:37:43', '6');
INSERT INTO `h_info_tki` VALUES ('366', '364', null, '240', 'Bagaimana apabila TKI yang bermasalah meminta penggantian rugi dana melebihi batas?', '<ul>\n	<li>\n		Permintaan ganti&nbsp; rugi ada dasar hukum agar TKI menuntut&nbsp; sesuai denga ketentuan hukum yang berlaku.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:37:45', '6', null, null, '2014-06-24 15:37:45', '6', '2014-06-24 15:37:45', '6', '2014-06-24 15:37:45', '6');
