INSERT INTO `h_info_tki` VALUES ('369', '367', null, '240', 'Apakah ada batasan berapa kali menjadi TKI ? misalnya dalam 3 bulan sudah 5 kali menjadi TKI.', '<ul>\n	<li>\n		Perlu dilakukan klarifikasi untuk mencari tahu alasan kepulangan.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:39:59', '6', null, null, '2014-06-24 15:39:59', '6', '2014-06-24 15:39:59', '6', '2014-06-24 15:39:59', '6');
INSERT INTO `h_info_tki` VALUES ('370', '368', null, '240', 'Apa saja Kewenangan Pengaduan BNP2TKI, Kemenakertrans, Perwakilan RI, Kemenhumham, Kemensos, Kemendagri, Kepolisian, Kemkes, Kem PP & PA, BP34TKI dan Pemda ?', '<ul>\n	<li>\n		BNP2TKI beserta unit-unit kerja didaerah jika menerima pengaduan permasalahan TKI mempunyai kewenangan menindaklanjuti dan memfasiltasi ke pihak terkait yang disesuaikan dengan permasalahan yang diadukan, jika itu terkait dengan instansi lain, maka kami berkoordinasi dengan instansi terkait.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 15:40:05', '6', null, null, '2014-06-24 15:40:05', '6', '2014-06-24 15:40:05', '6', '2014-06-24 15:40:05', '6');
INSERT INTO `h_info_tki` VALUES ('372', '1', null, '240', 'Pemalsuan umur TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 16:00:40', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('373', '141', null, '240', 'Bagaimana menangani / memberikan perlindungan terhadap TKI yang menjad i korban trafiking, korban kekerasan, kasusilaan, gaji tidak dibayar ?', '<ul>\n	<li>\n		BNP2TKI &nbsp;memfasilitasi pengaduan dan akan menangani permasalahan melalui koordinasi dengan pihak-pihak terkait (kepolisian, Kemnsos, Kemenkes dll).</li>\n</ul>\n', '4', '6', null, null, null, null, null, null, null, null, null, null, '2014-06-24 16:42:52', '6', null, null, '2014-06-24 16:42:52', '6', '2014-06-24 16:42:52', '6', '2014-06-24 16:42:52', '6');
INSERT INTO `h_info_tki` VALUES ('374', '372', null, '240', 'Bagaimana bila seorang TKI memberikan data palsu mengenai umur TKI tersebut, terjadilah perekrutan TKI yang dibawah umur bertentangan dengan UU No.39 TH 2004 pasal 35 huruf a bagaimana proses penyelesaian permasalahannya ?', '<ul>\n	<li>\n		BNP2TKI akan memproses pelaku pemalsuan tersebut kepada pihak yang berwajib.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 16:42:55', '6', null, null, '2014-06-24 16:42:55', '6', '2014-06-24 16:42:55', '6', '2014-06-24 16:42:55', '6');
INSERT INTO `h_info_tki` VALUES ('375', '26', null, '240', 'Bagaimana cara memberikan bantuan hukum terhadap TKI yang ditahan/dipenjara diluar negeri sedangkan  pengaturan undang-undang Negara kita berbeda dengan peraturan luar negeri ?', 'Apabila ada TKI/WNI yang ditahan/dipenjara maka Perwakilan RI akan melakukan bantuan hukum berupa pendampingan dengan menyewa pengacara setempat, sedang peran BNP2TKI adalah memonitor perkembangan permasalahan TKI dan siap untuk memfasilitasi apabila diperlukan&nbsp; data-data dukung dari dalam negeri maupun memberangkatkan keluarga atau pihak terkait dalam rangka pendampingan maupun saksi yang meringankan kasus TKI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 18:51:44', '6', null, null, '2014-06-24 18:51:44', '6', '2014-06-24 18:51:44', '6', '2014-06-24 18:51:44', '6');
INSERT INTO `h_info_tki` VALUES ('376', '353', null, '240', 'Bagaimanakah cara menyelesaikan mediasi dengan TKI ? bagaimana apabila dalam penyelesaian mediasi tidak menemui win-win solusion ? ', '<ul>\n	<li>\n		<strong>Mediasi pada dasarnya merupakan negosiasi yang mengikutsertakan pihak ketiga yang ahli </strong>&nbsp;(Mediator) yang dapat membantu para pihak dalam sengketa (CTKI/TKI/Keluarga dengan pihak yang dituntut)&nbsp; dengan mengkoordinasikan kegiatan-kegiatan, dan agar lebih efektif dalam bernegosiasi sehingga terselesaikan masalaha TKI.</li>\n	<li>\n		<strong>Apabila tidak menemui win-win solution mediator berhak menyarankan untuk menempuh jalur hukum/litigasi</strong><strong>.</strong></li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 18:51:46', '6', null, null, '2014-06-24 18:51:46', '6', '2014-06-24 18:51:46', '6', '2014-06-24 18:51:46', '6');
INSERT INTO `h_info_tki` VALUES ('377', '353', null, '240', 'Bagaimana apabila Permasalahan TKI sudah menemui win-win solusion dan sudah dibuat akta perdamain akan tetapi ditemukan pengaduan baru atau suatu saat pihak TKI merasa tidak puas dikemudian hari ?', '&nbsp;Maka dapat dilakukan mediasi ulang dengan menyertakan bukti-bukti mediasi terdahulu. Mediator dapat mempertanyakan kesepakatan yang tidak dilakukan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 18:51:48', '6', null, null, '2014-06-24 18:51:48', '6', '2014-06-24 18:51:48', '6', '2014-06-24 18:51:48', '6');
INSERT INTO `h_info_tki` VALUES ('378', '194', null, '240', 'Apakah permasalahan TKI yang illegal dapat perlindungan Hukum dari BNP2TKI ?', 'Dapat, TKI illegal hanya masalah prosedur penempatannya, jadi tetap dapat perlindungan sebagai warga negara, tentunya BNP2TKI memfasilitasikannya dan berkoordinasi dengan pihak-pihak terkait dui Indonesia dan di luar negeri sehinga hak TKI ybs terpenuhi dan terlindungi.&nbsp;&nbsp; ', '4', '3', null, null, null, null, null, null, null, null, null, null, '2014-06-24 20:19:12', '6', null, null, '2014-06-24 20:19:12', '6', '2014-06-24 20:19:12', '6', '2014-06-24 20:19:12', '6');
INSERT INTO `h_info_tki` VALUES ('379', '359', null, '240', 'Bagaimana bila TKI yang dipulangkan dari luar negeri meninggal dunia dengan alasan TKI tersebut bunuh diri atau kecelakaan kerja dan pihak keluarga TKI melakuan visum et reverendum yang ditemukan bukti barub ahwa TKI tersebut terbukti organ tubuh Tk Itersebut tidak utuh, bagaimana mengatasinya ?', '<ul>\n	<li>\n		Melakukan klarifikasi kebenaranya dan&nbsp; Melakukan legalisasi terhadap hasil visum et reverendum, berkoordinasi dengan Kemenlu RI dan Perwakilan RI di Negara penempatan untuk melakukan pelaporan dan penuntutan;</li>\n</ul>\n<ul>\n	<li>\n		Isu pengambilan organ tubuh selama ini tidak pernah terbukti.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 20:20:57', '6', null, null, '2014-06-24 20:20:57', '6', '2014-06-24 20:20:57', '6', '2014-06-24 20:20:57', '6');
INSERT INTO `h_info_tki` VALUES ('380', '356', null, '240', 'Bagaimana penyelesaian bila menemukan TKI yang bermasalah di luar negeri akan tetapi TKI tersebut tidak dapat memberikan keterangan dengan jelas / sikap diam dikarenakan TKI tersebut depresi karna pulang dari luar negeri ?', 'Meminta klarifikasi dari perusahaan yang memberangkatkan TKI tersebut ke luar negeri dan meminta untuk melakukan klaim asuransi untuk pengobatan TKI tersebut. Apabila tidak ada PPTKIS dan asuransi maka BNP2TKI memfasilitasi pengobatan TKI tersebut di RS. POLRI Pusat.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 20:20:59', '6', null, null, '2014-06-24 20:20:59', '6', '2014-06-24 20:20:59', '6', '2014-06-24 20:20:59', '6');
INSERT INTO `h_info_tki` VALUES ('381', '360', null, '240', 'Bagaimana mekanisme penyelesaian apabila penempatannya TKI tidak sesuai perjanjian, misalnya TKI tersebut diperjanjian dikerjakan di sebuah pabrik nyatanya setelah sampai di luar negeri dipekerjakan sebagai Pekerja sex komersial ?', 'Jika terbukti kepada pelaku penempatan untuk kemudian diproses/ ditunda secara hukum;<br />\nTKI diselamatkan dan dipulangkan ke Indonesia', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-24 20:23:01', '6', null, null, '2014-06-24 20:23:01', '6', '2014-06-24 20:23:01', '6', '2014-06-24 20:23:01', '6');
INSERT INTO `h_info_tki` VALUES ('382', '363', null, '240', 'Bagaimanakah langkah-langkah mengurus gaji TKI, Asuransi TKI, dan Penggantian rugi TKI ?', '<ol style=\"list-style-type:lower-alpha;\">\n	<li>\n		Mengurus gaji:</li>\n</ol>\n<ul>\n	<li>\n		Bila TKI berada di Indonesia, mengurus melalui PPTKIS untuk dimintakan melalui agensi.</li>\n	<li>\n		Bila TKI berada di luar negeri, menghubungi Perwakilan RI setempat.</li>\n</ul>\n<ol style=\"list-style-type:lower-alpha;\">\n	<li value=\"2\">\n		Asuransi dan ganti rugi:</li>\n</ol>\n<ul>\n	<li>\n		Bila TKI berada di Indonesia, mengajukan klaim asuransi sesuai syarat-syarat yang ditentukan.</li>\n</ul>\n', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-24 20:24:23', '6', null, null, '2014-06-24 20:24:23', '6', '2014-06-24 20:24:23', '6', '2014-06-24 20:24:23', '6');
INSERT INTO `h_info_tki` VALUES ('383', '386', null, '240', 'Apakah TKI di disetiap Negara mempunyai organisasi untuk memantau kedaan setiap TKI dan apakah setiap organisasi tersebut selalu berkordinasi dengan BNP2TKI dengan memberikan aduan dan keadaan masing-masingTKI ?', 'Belum semua negara mempunyai hal seperti itu dan kedepan akan dibangun jejaring seperti itu sehingga lebih efisian dan efektif.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 10:21:03', '6', '2014-06-25 10:36:27', '6', '2014-06-25 10:21:03', '6', '2014-06-25 10:21:03', '6', '2014-06-25 10:36:27', '6');
INSERT INTO `h_info_tki` VALUES ('384', '367', null, '240', 'Apakah ada batasan berapa kali menjadi TKI ? misalnya dalam 3 bulan sudah 5 kali menjadi TKI ?', 'Perlu dilakukan klarifikasi untuk mencari tahu alasan kepulangan.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 10:21:06', '6', null, null, '2014-06-25 10:21:06', '6', '2014-06-25 10:21:06', '6', '2014-06-25 10:21:06', '6');
INSERT INTO `h_info_tki` VALUES ('385', '387', null, '240', 'Lembaga apa saja yang berhubungan dengan BNP2TKI ?', '<ol style=\"list-style-type:lower-alpha;\">\n	<li>\n		Lembaga Pemerintah :</li>\n</ol>\n<ul>\n	<li>\n		Kementerian Tenaga Kerja dan Transmigrasi</li>\n	<li>\n		Kementerian Luar Negeri</li>\n	<li>\n		Kementerian Perhubungan</li>\n	<li>\n		Kementerian Hukum dan Ham</li>\n	<li>\n		Kementerian Sosial</li>\n	<li>\n		Kementerian Kesehatan</li>\n	<li>\n		Kepolisian RI</li>\n	<li>\n		Perwakilan RI</li>\n</ul>\n<ol style=\"list-style-type:lower-alpha;\">\n	<li value=\"2\">\n		Lembaga non Pemerintah :</li>\n</ol>\n<ul>\n	<li>\n		Asuransi</li>\n	<li>\n		PPTKIS</li>\n	<li>\n		Perbankan</li>\n	<li>\n		BLK-LN</li>\n	<li>\n		Sarana Kesehatan (Sarkes)</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 10:21:08', '6', '2014-06-25 10:36:31', '6', '2014-06-25 10:21:08', '6', '2014-06-25 10:21:08', '6', '2014-06-25 10:36:31', '6');
