INSERT INTO `h_info_tki` VALUES ('389', '388', null, '240', 'Bagaimanakah penanganan pemerintah untuk kasus yang sudah lama terbengkalai atau sudah dalam waktu lama ?', '<ul>\n	<li>\n		Kasus tersebut akan ditanda tangani dan jika diperlukan akan dilakukan dengan melengkapi data baru dan berkoordinasi dengan instansi terkait.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 10:36:33', '6', null, null, '2014-06-25 10:36:33', '6', '2014-06-25 10:36:33', '6', '2014-06-25 10:36:33', '6');
