INSERT INTO `h_info_tki` VALUES ('413', '1', null, '240', 'Penipuan TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:17:34', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('414', '408', null, '240', 'Apakah jaminan bagi para TKI yang telah menjadi korban tindak kekerasan dan pelecehan seksual hingga TKI menderita sakit dan gangguan psikologi tersebut mendapat perlindungan hukum?', '<ul>\n	<li>\n		Pemerintah memfasilitasi TKI untuk mendapatkan hak-haknya;</li>\n	<li>\n		Pemerintah membantu TKI untuk memproses pelaku kepada pihak yang berwajib;</li>\n	<li>\n		Pemerintah membantu menyediakan, memfasilitasi pemulihan kesehatan.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:20:19', '6', null, null, '2014-06-25 11:20:19', '6', '2014-06-25 11:20:19', '6', '2014-06-25 11:20:19', '6');
INSERT INTO `h_info_tki` VALUES ('415', '412', null, '240', 'Apakah para TKI yang menjadi korban kekerasan yang dilakukan oleh majikannya di luar negeri itu selain mendapatkan asuransi apakah majikannya yang telah menjadi pelaku tindak kekerasan yang dilakukan kepada TKI tersebut harus membayar ganti rugi dan mendapat hukuman yang sesuai dengan undang-undang yang ada dalam perjanjian hukum yang telah ditandatangani oleh para TKI dan majikanya dalam perjanjian kerjanya?', '<ul>\n	<li>\n		Pelaku kekerasan tindak pidana akan di proses secara hukum;</li>\n	<li>\n		Tuntutan ganti rugi secara perdata apabila pelaku/ pengguna sudah ditetapkan secara pidana.</li>\n</ul>\n', '4', '2', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:20:20', '6', null, null, '2014-06-25 11:20:20', '6', '2014-06-25 11:20:20', '6', '2014-06-25 11:20:20', '6');
INSERT INTO `h_info_tki` VALUES ('416', '413', null, '240', 'Tingginya tingkat penipuan yang telah dilakukan perusahaan penyalur untuk tenaga kerja Indonesia yang di tempatkan di luar negeri, bagaimana cara jitu yang harus dilakukan pemerintah untuk menekan tingginya tingkat penipuan tersebut?', 'Dari pihak Pemerintah melakukan sosialisasi tentang perlindungan TKI dan melakukan pembinaan dan pengawasan, tindakan terhadap perlindungan TKI.<br />\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:20:22', '6', null, null, '2014-06-25 11:20:22', '6', '2014-06-25 11:20:22', '6', '2014-06-25 11:20:22', '6');
