INSERT INTO `h_info_tki` VALUES ('419', '1', null, '240', 'Percaloan TKI', '', '1', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:22:10', '6', null, null, null, null, null, null, null, null);
INSERT INTO `h_info_tki` VALUES ('420', '417', null, '240', 'Bagaimana agar membuat para calon TKI itu mengerti dan percaya kepada pemerintah dalam melaksanakan perlindungan dan pengawasan terhadap para TKI, agar tidak terjadi kembali permasalahan yang diderita oleh para TKI akhir-akhir ini?', '<ul>\n	<li>\n		Memberikan sosialisasi mengenai peningkatan penyadaran masyarakat Secara umum tentang bekerja di luar negeri secara benar dan aman;</li>\n	<li>\n		Melakukan penindakan kepada pelaku pelanggaran agar timbul kepercayaan dari masyarakat kepada pemerintah.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:25:44', '6', null, null, '2014-06-25 11:25:44', '6', '2014-06-25 11:25:44', '6', '2014-06-25 11:25:44', '6');
INSERT INTO `h_info_tki` VALUES ('421', '418', null, '240', 'Langkah-langkah apa saja yang harus diperhatikan ketika para TKI yang akan ditempatkan bekerja di luar negeri agar terhindar dari kejadian atau tindakan kekerasan dan hal-hal lain ? serta dimana peran BNP2TKI dalam keikutsertaan untuk pencegahan semua tindak dan kejadian kekerasan yang terjadi di Negara penempatan?', '<ul>\n	<li>\n		Memberikan sosialisasi dan;</li>\n	<li>\n		Memberikan pemahaman melalui Pembekalan Akhir Pemberangkatan (PAP) dengan tujuan mengetahui manfaat Perjanjian Kerja antara kedua belah pihak.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:25:46', '6', null, null, '2014-06-25 11:25:46', '6', '2014-06-25 11:25:46', '6', '2014-06-25 11:25:46', '6');
INSERT INTO `h_info_tki` VALUES ('422', '419', null, '240', 'Bagaimana cara dan pencegahan yang harus dilakukan BNP2TKI untuk menanggulangi praktik percaloan yang marak dalam perekrutan calon TKI?', '<ul>\n	<li>\n		Pelayanan dengan sistem online, PRC TKI terdaftar di dinas kabupaten/kota.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:25:48', '6', null, null, '2014-06-25 11:25:48', '6', '2014-06-25 11:25:48', '6', '2014-06-25 11:25:48', '6');
