INSERT INTO `h_info_tki` VALUES ('425', '396', null, '240', 'Bagaimana dengan aturan yang hukum dan Undang-Undang mewajibkan TKI melapor ke Perwakilan RI di luar negeri sebelum melakukan kepulangannya secara mandiri?', 'Undang-undang No. 39 Tahun 2004, Pasal 74 menyatakan bahwa :<br />\n<ol>\n	<li>\n		Setiap TKI yang akan kembali ke Indonesia wajib melaporkan kepulangannya kepada Perwakilan Republik Indonesia negera tujuan.</li>\n	<li>\n		Pelaporan bagi TKI yang bekerja pada Pengguna perseorangan dilakukan oleh pelaksana penempatan TKI swasta.</li>\n</ol>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:28:43', '6', null, null, '2014-06-25 11:28:43', '6', '2014-06-25 11:28:43', '6', '2014-06-25 11:28:43', '6');
INSERT INTO `h_info_tki` VALUES ('426', '423', null, '240', 'Apabila Terdapat keluhan pengaduan yang muncul terhadap TKI siapa Terlebih dahulu yang harus mengurusnya?', '<ul>\n	<li>\n		PPTKIS yang menempatkan.</li>\n</ul>\n', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:28:45', '6', null, null, '2014-06-25 11:28:45', '6', '2014-06-25 11:28:45', '6', '2014-06-25 11:28:45', '6');
INSERT INTO `h_info_tki` VALUES ('427', '424', null, '240', 'Dalam bentuk apa saja kerjasama BNP2TKI dan POLRI dalam penanganan TKI?', 'MOU dengan RS POLRI.', '4', '0', null, null, null, null, null, null, null, null, null, null, '2014-06-25 11:28:47', '6', null, null, '2014-06-25 11:28:47', '6', '2014-06-25 11:28:47', '6', '2014-06-25 11:28:47', '6');
