INSERT INTO `h_info_tki` VALUES ('518', '157', null, '126', 'jika TKI melamar pekerjaan melalui web jobsinfo.bnp2tki.go.id apakah TKI mendapat pelatihan bahasa? jika dapat, siapa yg akan memberikan pelatihan bahasanya?', 'Untuk Skema Penempatan P to P,&nbsp; BNP2TKI tidak memfasilitasi pelatihan bahasa<br />\n<br />\n<br />\n', '4', '4', null, null, null, null, null, null, null, null, null, null, '2017-03-03 09:24:46', '137', '2017-03-22 16:41:16', '133', '2017-03-08 14:44:50', '121', '2017-03-22 16:41:22', '133', null, null);
INSERT INTO `h_info_tki` VALUES ('519', '179', null, '173', 'persyatratan pembuatan SIP (surat izin pengerahan) dan masa berlakunya? ', 'Persyaratan :<br />\n1. Dokumen <em>online</em> Perjanjian Kerjasama Penempatan;<br />\n2. <em><em>Job Order/ Demand Letter;</em></em><br />\n3. Rancangan Perjanjian Kerja yang sudah disahkan oleh Perwakilan RI.<br />\n<br />\nProsedur :<br />\n1. PPTKIS mengajukan permohonan kepada Direktur Penyiapan dan Pembekalan Pemberangkatan melalui SISKOTKLN;<br />\n2. Verifikasi Data online Perjanjian Kerjasama Penempatan, <em>Job Order/ Demand Letter</em>, rancangan Perjanjian Kerja yang sudah disahkan oleh Perwakilan RI.<br />\n<br />\nMasa Berlaku :<br />\nMaksimal 6 (enam) bulan (tergantung masa berlaku <em>Job Order/ Demand Letter</em>). ', '4', '0', null, null, null, null, null, null, null, null, null, null, '2017-03-09 09:16:04', '137', '2017-03-22 16:37:50', '134', '2017-03-13 10:54:50', '122', '2017-03-22 16:37:58', '134', null, null);
